import React, { useState } from "react";

export default function Dashboard() {
  const [activeMenu, setActiveMenu] = useState("Dashboard");
  const [selectedCategory, setSelectedCategory] = useState(null); // penyelidikan / penyidikan

  const menuItems = [
    { name: "Dashboard", icon: "📊" },
    { name: "DIR RES. SIBER", icon: "📁" },
    { name: "Input Data", icon: "📈" },
    { name: "Preview Surat", icon: "📄" },
  ];

  // Pilihan submenu untuk DIR RES. SIBER
  const dirResSiberOptions = [
    { key: "penyelidikan", label: "Surat Penyelidikan" },
    { key: "penyidikan", label: "Surat Penyidikan" },
  ];

  // Handler ketika pilih kategori surat di DIR RES. SIBER
  function handleSelectCategory(key) {
    setSelectedCategory(key);
    setActiveMenu("Input Data"); // langsung navigasi ke Input Data
  }

  return (
    <div className="flex min-h-screen">
      {/* Sidebar */}
      <div className="bg-white w-64 px-6 py-8 flex flex-col">
        <h1 className="text-xl font-bold text-blue-900 mb-10">e-Perkaba PMJ</h1>
        <h3 className="font-semibold text-gray-700 mb-4">Menu</h3>

        {/* Menu Utama */}
        <nav className="flex flex-col space-y-3 mb-8">
          {menuItems.map(({ name, icon }) => (
            <button
              key={name}
              onClick={() => setActiveMenu(name)}
              className={`flex items-center space-x-2 px-4 py-2 rounded-md border font-semibold text-sm
                ${
                  activeMenu === name
                    ? "bg-blue-900 text-white border-blue-900"
                    : "border border-pink-300 text-pink-500 hover:bg-pink-50"
                }`}
            >
              <span>{icon}</span>
              <span>{name}</span>
            </button>
          ))}
        </nav>

        {/* Submenu DIR RES. SIBER */}
        {activeMenu === "DIR RES. SIBER" && (
          <div className="flex justify-center space-x-6 mt-auto">
            {dirResSiberOptions.map(({ key, label }) => (
              <button
                key={key}
                onClick={() => handleSelectCategory(key)}
                className="border border-pink-400 text-pink-600 font-semibold rounded-md px-3 py-1 hover:bg-pink-50"
              >
                {label}
              </button>
            ))}
          </div>
        )}
      </div>

      {/* Main Content */}
      <div className="flex-1 bg-blue-900 text-white p-8 relative">
        {/* Header - Profile */}
        <div className="absolute top-6 right-6 flex items-center space-x-3">
          <div className="text-right">
            <div className="font-bold">Karina</div>
            <div className="text-xs text-blue-300">Administrator</div>
          </div>
          <img
            src="https://i.pravatar.cc/40?img=47"
            alt="Profile"
            className="w-10 h-10 rounded-full"
          />
        </div>

        {/* Title */}
        <h2 className="text-lg font-bold mb-6">{activeMenu}</h2>

        {/* Konten berdasarkan menu */}
        {activeMenu === "DIR RES. SIBER" && (
          <p className="text-pink-300">
            Silahkan pilih kategori surat di bawah menu sebelah kiri.
          </p>
        )}

        {activeMenu === "Input Data" && (
          <>
            <div className="mb-6 p-3 bg-blue-800 rounded-md font-semibold w-48">
              Kategori Surat:{" "}
              {selectedCategory === "penyelidikan"
                ? "Surat Penyelidikan"
                : selectedCategory === "penyidikan"
                ? "Surat Penyidikan"
                : "Belum dipilih"}
            </div>

            {/* Contoh form input data */}
            <form className="space-y-4 max-w-md">
              <div>
                <label className="block mb-1">Nomor Surat</label>
                <input
                  type="text"
                  placeholder="Masukkan nomor surat"
                  className="w-full p-2 rounded text-black"
                />
              </div>
              <div>
                <label className="block mb-1">Tanggal Surat</label>
                <input
                  type="date"
                  className="w-full p-2 rounded text-black"
                />
              </div>
              {/* Tambah form sesuai kebutuhan */}
              <button
                type="submit"
                className="bg-pink-500 px-4 py-2 rounded font-semibold hover:bg-pink-600"
              >
                Simpan
              </button>
            </form>
          </>
        )}

        {activeMenu === "Dashboard" && (
          <p>Selamat datang di Dashboard</p>
        )}

        {activeMenu === "Preview Surat" && (
          <p>Preview Surat akan ditampilkan di sini.</p>
        )}
      </div>
    </div>
  );
}
