import React, { useState } from "react";
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

export default function PenyelidikanForm() {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    // Step 1
    noLp: "",
    nama: "",
    noIdentitas: "",
    kewarganegaraan: "",
    jenisKelamin: "",
    // Step 2
    tempatLahir: "",
    tanggalLahir: "",
    pekerjaan: "",
    agama: "",
    alamat: "",
    noTelepon: "",
    // Step 3
    hariKejadian: "",
    tanggalKejadian: "",
    pukulJam: "",
    pukulMenit: "",
    zonaWaktu: "WIB",
    tempatKejadian: "",
    apaYangTerjadi: "",
    // Step 4
    siapaTerlapor: "",
    terlaporNama: "",
    terlaporNoIdentitas: "",
    terlaporKewarganegaraan: "",
    terlaporSuku: "",
    terlaporJenisKelamin: "",
    terlaporTempatLahir: "",
    terlaporTanggalLahir: "",
    terlaporUmurTahun: "",
    terlaporUmurBulan: "",
    terlaporUmurHari: "",
    terlaporPekerjaan: "",
    terlaporAgama: "",
    terlaporAlamat: "",
    terlaporNoTelp: "",
    terlaporMediaSosial: "",
    terlaporKorporasi: false,
    // Step 5 - korban
    korbanNama: "",
    korbanNoIdentitas: "",
    korbanKewarganegaraan: "",
    korbanSuku: "",
    korbanJenisKelamin: "",
    korbanTempatLahir: "",
    korbanTanggalLahir: "",
    korbanUmurTahun: "",
    korbanUmurBulan: "",
