  const handleDownloadPDF = () => {
    const input = document.getElementById('surat-laporan-polisi-preview');

    if (!input) {
      alert("Elemen untuk di-download tidak ditemukan!");
      return;
    }

    // Set scale to 3 for higher resolution screenshot
    html2canvas(input, { scale: 3, useCORS: true, allowTaint: true })
      .then((canvas) => {
        const imgData = canvas.toDataURL('image/jpeg', 1.0);
        const pdf = new jsPDF({
          orientation: 'portrait',
          unit: 'mm',
          format: 'a4'
        });

        const pdfWidth = pdf.internal.pageSize.getWidth();
        const pdfHeight = pdf.internal.pageSize.getHeight();
        const imgWidth = canvas.width;
        const imgHeight = canvas.height;

        const ratio = imgHeight / imgWidth;
        let imgFinalHeight = pdfWidth * ratio;

        // Ensure the image fits within a single page, scaling down if too tall
        if (imgFinalHeight > pdfHeight) {
          imgFinalHeight = pdfHeight;
          const imgFinalWidth = pdfHeight / ratio;
          // Center horizontally
          pdf.addImage(imgData, 'JPEG', (pdfWidth - imgFinalWidth) / 2, 0, imgFinalWidth, imgFinalHeight);
        } else {
          // If it fits, center it horizontally and vertically
          const xOffset = (pdfWidth - pdfWidth) / 2; // Should be 0 for full width
          const yOffset = (pdfHeight - imgFinalHeight) / 2;
          pdf.addImage(imgData, 'JPEG', xOffset, yOffset, pdfWidth, imgFinalHeight);
        }
        
        pdf.save(`Laporan-Polisi-${formData.noLp || 'dokumen'}.pdf`);
      }).catch(err => {
        console.error("Error generating PDF:", err);
        alert("Terjadi kesalahan saat membuat PDF. Mohon coba lagi.");
