import React, { useState } from "react";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

export default function PenyelidikanForm() {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    // Step 1
    noLp: "",
    nama: "",
    noIdentitas: "",
    kewarganegaraan: "",
    jenisKelamin: "",
    // Step 2
    tempatLahir: "",
    tanggalLahir: "",
    pekerjaan: "",
    agama: "",
    alamat: "",
    noTelepon: "",
    // Step 3
    hariKejadian: "",
    tanggalKejadian: "",
    pukulJam: "",
    pukulMenit: "",
    zonaWaktu: "WIB",
    tempatKejadian: "",
    apaYangTerjadi: "",
    // Step 4
    siapaTerlapor: "",
    terlaporNama: "",
    terlaporNoIdentitas: "",
    terlaporKewarganegaraan: "",
    terlaporSuku: "",
    terlaporJenisKelamin: "",
    terlaporTempatLahir: "",
    terlaporTanggalLahir: "",
    terlaporUmurTahun: "",
    terlaporUmurBulan: "",
    terlaporUmurHari: "",
    terlaporPekerjaan: "",
    terlaporAgama: "",
    terlaporAlamat: "",
    terlaporNoTelp: "",
    terlaporMediaSosial: "",
    terlaporKorporasi: false,
    // Step 5 - korban
    korbanNama: "",
    korbanNoIdentitas: "",
    korbanKewarganegaraan: "",
    korbanSuku: "",
    korbanJenisKelamin: "",
    korbanTempatLahir: "",
    korbanTanggalLahir: "",
    korbanUmurTahun: "",
    korbanUmurBulan: "",
    korbanUmurHari: "",
    korbanPekerjaan: "",
    korbanAgama: "",
    korbanAlamat: "",
    korbanNoTelp: "",
    korbanMediaSosial: "",
    korbanKorporasi: false,
    // Kapan dilaporkan (step 5)
    kapanDilaporkanHari: "",
    kapanDilaporkanTanggal: "",
    kapanDilaporkanJam: "",
    kapanDilaporkanMenit: "",
    kapanDilaporkanZonaWaktu: "WIB",
    // Pasal yang dipilih (step 6)
    selectedPasal: [],
    //  Tindak pidana apa (step 7)
    tindakPidana: "",
    //  Saksi (step 8)
    saksi: [],
    // Barang Bukti (step 9)
    barangBukti: "",
    // Uraian singkat yang dilaporkan (step 10)
    uraianSingkat: "",
    isAnonymous: false,
  });

  const [currentSaksi, setCurrentSaksi] = useState({
    nama: "",
    noIdentitas: "",
    kewarganegaraan: "",
    suku: "",
    jenisKelamin: "",
    tempatLahir: "",
    tanggalLahir: "",
    umurTahun: "",
    umurBulan: "",
    umurHari: "",
    pekerjaan: "",
    agama: "",
    alamat: "",
    noTelp: "",
    mediaSosial: "",
  });

  const [errors, setErrors] = useState({});

  const availablePasal = [
    { id: "pasal_45_27_1", text: "Pasal 45 Jo. Pasal 27 ayat (1)" },
    { id: "pasal_45_27_2", text: "Pasal 45 Jo. Pasal 27 ayat (2)" },
    { id: "pasal_45_27a", text: "Pasal 45 Jo. Pasal 27A" },
    { id: "pasal_45_27b_1", text: "Pasal 45 Jo. Pasal 27B ayat (1)" },
    { id: "pasal_45_27b_2", text: "Pasal 45 Jo. Pasal 27B ayat (2)" },
    { id: "pasal_45a_28_1", text: "Pasal 45A Jo. Pasal 28 ayat (1)" },
    { id: "pasal_45a_28_2", text: "Pasal 45A Jo. Pasal 28 ayat (2)" },
    { id: "pasal_45a_29_1", text: "Pasal 45A Jo. Pasal 29 ayat (1)" },
    { id: "pasal_45b_29", text: "Pasal 45B Jo. Pasal 29" },
    { id: "pasal_46_30_1", text: "Pasal 46 Jo. Pasal 30 ayat (1)" },
    { id: "pasal_46_30_2", text: "Pasal 46 Jo. Pasal 30 ayat (2)" },
    { id: "pasal_47_31_1", text: "Pasal 47 Jo. Pasal 31 ayat (1)" },
    { id: "pasal_47_31_2", text: "Pasal 47 Jo. Pasal 31 ayat (2)" },
    { id: "pasal_48_32_1", text: "Pasal 48 Jo. Pasal 32 ayat (1)" },
    { id: "pasal_48_32_2", text: "Pasal 48 Jo. Pasal 32 ayat (2)" },
    { id: "pasal_48_32_3", text: "Pasal 48 Jo. Pasal 32 ayat (3)" },
    { id: "pasal_49_33", text: "Pasal 49 Jo. Pasal 33" },
    { id: "pasal_50_34_1", text: "Pasal 50 Jo. Pasal 34 ayat (1)" },
    { id: "pasal_51_35", text: "Pasal 51 Jo. Pasal 35" },
    { id: "pasal_51_36", text: "Pasal 51 Jo. Pasal 36" },
  ];

  // Validation functions for each step (same as original)
  const validateStep1 = () => {
    const newErrors = {};
    if (!formData.noLp) newErrors.noLp = "Nomor LP wajib diisi";
    if (!formData.nama) newErrors.nama = "Nama wajib diisi";
    if (!formData.noIdentitas) newErrors.noIdentitas = "No. Identitas wajib diisi";
    if (!formData.kewarganegaraan) newErrors.kewarganegaraan = "Pilih kewarganegaraan";
    if (!formData.jenisKelamin) newErrors.jenisKelamin = "Pilih jenis kelamin";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep2 = () => {
    const newErrors = {};
    if (!formData.tempatLahir) newErrors.tempatLahir = "Tempat lahir wajib diisi";
    if (!formData.tanggalLahir) newErrors.tanggalLahir = "Tanggal lahir wajib diisi";
    if (!formData.pekerjaan) newErrors.pekerjaan = "Pekerjaan wajib diisi";
    if (!formData.agama) newErrors.agama = "Agama wajib dipilih";
    if (!formData.alamat) newErrors.alamat = "Alamat wajib diisi";
    if (!formData.noTelepon) newErrors.noTelepon = "No. telepon wajib diisi";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep3 = () => {
    const newErrors = {};
    if (!formData.hariKejadian) newErrors.hariKejadian = "Hari kejadian wajib diisi";
    if (!formData.tanggalKejadian) newErrors.tanggalKejadian = "Tanggal kejadian wajib diisi";
    if (!formData.pukulJam) newErrors.pukulJam = "Jam wajib diisi";
    if (!formData.pukulMenit) newErrors.pukulMenit = "Menit wajib dipilih";
    if (!formData.zonaWaktu) newErrors.zonaWaktu = "Zona waktu wajib dipilih";
    if (!formData.tempatKejadian) newErrors.tempatKejadian = "Tempat kejadian wajib diisi";
    if (!formData.apaYangTerjadi) newErrors.apaYangTerjadi = "Harap jelaskan apa yang terjadi";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep4 = () => {
    const newErrors = {};
    if (!formData.siapaTerlapor) newErrors.siapaTerlapor = "Pilih siapa yang terlapor";
    if (formData.siapaTerlapor === "Terlapor") {
      if (!formData.terlaporNama) newErrors.terlaporNama = "Nama/Alias wajib diisi";
      if (!formData.terlaporNoIdentitas) newErrors.terlaporNoIdentitas = "No. Identitas wajib diisi";
      if (!formData.terlaporKewarganegaraan) newErrors.terlaporKewarganegaraan = "Pilih kewarganegaraan";
      if (!formData.terlaporSuku) newErrors.terlaporSuku = "Suku wajib diisi";
      if (!formData.terlaporJenisKelamin) newErrors.terlaporJenisKelamin = "Pilih jenis kelamin";
      if (!formData.terlaporTempatLahir) newErrors.terlaporTempatLahir = "Tempat lahir wajib diisi";
      if (!formData.terlaporTanggalLahir) newErrors.terlaporTanggalLahir = "Tanggal lahir wajib diisi";
      if (!formData.terlaporUmurTahun) newErrors.terlaporUmurTahun = "Umur Tahun wajib diisi";
      if (!formData.terlaporPekerjaan) newErrors.terlaporPekerjaan = "Pekerjaan wajib diisi";
      if (!formData.terlaporAgama) newErrors.terlaporAgama = "Pilih agama";
      if (!formData.terlaporAlamat) newErrors.terlaporAlamat = "Alamat wajib diisi";
      if (!formData.terlaporNoTelp) newErrors.terlaporNoTelp = "No. Telp wajib diisi";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep5 = () => {
    const newErrors = {};
    if (!formData.korbanNama) newErrors.korbanNama = "Nama/Alias wajib diisi";
    if (!formData.korbanNoIdentitas) newErrors.korbanNoIdentitas = "No. Identitas wajib diisi";
    if (!formData.korbanKewarganegaraan) newErrors.korbanKewarganegaraan = "Pilih kewarganegaraan";
    if (!formData.korbanSuku) newErrors.korbanSuku = "Suku wajib diisi";
    if (!formData.korbanJenisKelamin) newErrors.korbanJenisKelamin = "Pilih jenis kelamin";
    if (!formData.korbanTempatLahir) newErrors.korbanTempatLahir = "Tempat lahir wajib diisi";
    if (!formData.korbanTanggalLahir) newErrors.korbanTanggalLahir = "Tanggal lahir wajib diisi";
    if (!formData.korbanUmurTahun) newErrors.korbanUmurTahun = "Umur Tahun wajib diisi";
    if (!formData.korbanPekerjaan) newErrors.korbanPekerjaan = "Pekerjaan wajib diisi";
    if (!formData.korbanAgama) newErrors.korbanAgama = "Pilih agama";
    if (!formData.korbanAlamat) newErrors.korbanAlamat = "Alamat wajib diisi";
    if (!formData.korbanNoTelp) newErrors.korbanNoTelp = "No. Telp wajib diisi";
    if (!formData.kapanDilaporkanHari) newErrors.kapanDilaporkanHari = "Hari wajib diisi";
    if (!formData.kapanDilaporkanTanggal) newErrors.kapanDilaporkanTanggal = "Tanggal wajib diisi";
    if (!formData.kapanDilaporkanJam) newErrors.kapanDilaporkanJam = "Jam wajib diisi";
    if (!formData.kapanDilaporkanMenit) newErrors.kapanDilaporkanMenit = "Menit wajib diisi";
    if (!formData.kapanDilaporkanZonaWaktu) newErrors.kapanDilaporkanZonaWaktu = "Zona waktu wajib dipilih";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep6 = () => {
    const newErrors = {};
    if (formData.selectedPasal.length === 0) newErrors.selectedPasal = "Minimal pilih satu pasal";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep7 = () => {
    const newErrors = {};
    if (!formData.tindakPidana) newErrors.tindakPidana = "Uraian tindak pidana wajib diisi.";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep8 = () => {
    const newErrors = {};
    if (formData.saksi.length === 0) newErrors.saksi = "Minimal harus ada satu saksi yang ditambahkan.";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep9 = () => {
    const newErrors = {};
    if (!formData.barangBukti) newErrors.barangBukti = "Barang bukti wajib diisi.";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep10 = () => {
    const newErrors = {};
    if (!formData.uraianSingkat) newErrors.uraianSingkat = "Uraian singkat wajib diisi.";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handlePasalChange = (e) => {
    const { id, checked } = e.target;
    setFormData(prev => {
      const newSelectedPasal = [...prev.selectedPasal];
      if (checked && !newSelectedPasal.includes(id)) {
        newSelectedPasal.push(id);
      } else if (!checked) {
        const index = newSelectedPasal.indexOf(id);
        if (index > -1) {
          newSelectedPasal.splice(index, 1);
        }
      }
      return { ...prev, selectedPasal: newSelectedPasal };
    });
  };

  const handleSaksiChange = (e) => {
    const { name, value } = e.target;
    setCurrentSaksi(prev => ({ ...prev, [name]: value }));
  };

  const handleAddSaksi = (e) => {
    e.preventDefault();
    if (!currentSaksi.nama) {
      alert("Nama saksi tidak boleh kosong.");
      return;
    }
    setFormData(prev => ({ ...prev, saksi: [...prev.saksi, currentSaksi] }));
    setCurrentSaksi({
      nama: '',
      noIdentitas: '',
      kewarganegaraan: '',
      suku: '',
      jenisKelamin: '',
      tempatLahir: '',
      tanggalLahir: '',
      umurTahun: '',
      umurBulan: '',
      umurHari: '',
      pekerjaan: '',
      agama: '',
      alamat: '',
      noTelp: '',
      mediaSosial: '',
    });
  };

  const handleDownloadPDF = () => {
    const input = document.getElementById('surat-laporan-polisi-preview');
    if (!input) {
      alert("Elemen untuk di-download tidak ditemukan!");
      return;
    }
    html2canvas(input, { scale: 2, useCORS: true }).then(canvas => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF({
        orientation: 'portrait',
        unit: 'mm',
        format: 'a4'
      });
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = pdf.internal.pageSize.getHeight();
      const canvasWidth = canvas.width;
      const canvasHeight = canvas.height;
      const ratio = canvasWidth / canvasHeight;
      let imgFinalWidth = pdfWidth;
      let imgFinalHeight = pdfWidth / ratio;
      if (imgFinalHeight > pdfHeight) {
        imgFinalHeight = pdfHeight;
        imgFinalWidth = imgFinalHeight * ratio;
      }
      const margin = 10;
      const finalWidthWithMargin = pdfWidth - (margin * 2);
      const finalHeightWithMargin = finalWidthWithMargin / ratio;
      const x = margin;
      const y = (pdfHeight - finalHeightWithMargin) / 2;
      pdf.addImage(imgData, 'PNG', x, y, finalWidthWithMargin, finalHeightWithMargin);
      pdf.save(`Laporan-Polisi-${formData.noLp || 'dokumen'}.pdf`);
    });
  };

  const handleNext = (e) => {
    e.preventDefault();
    if (step === 1 && validateStep1()) {
      setErrors({});
      setStep(2);
    } else if (step === 2 && validateStep2()) {
      setErrors({});
      setStep(3);
    } else if (step === 3 && validateStep3()) {
      setErrors({});
      setStep(4);
    } else if (step === 4 && validateStep4()) {
      setErrors({});
      setStep(5);
    } else if (step === 5 && validateStep5()) {
      setErrors({});
      setStep(6);
    } else if (step === 6 && validateStep6()) {
      const selectedPasalText = formData.selectedPasal
        .map(id => {
          const pasal = availablePasal.find(p => p.id === id);
          return pasal ? pasal.text : '';
        })
        .join(' dan ');
      setFormData(prev => ({
        ...prev,
        tindakPidana: selectedPasalText ? `sebagaimana dimaksud dalam ${selectedPasalText}` : ''
      }));
      setErrors({});
      setStep(7);
    } else if (step === 7 && validateStep7()) {
      setErrors({});
      setStep(8);
    } else if (step === 8 && validateStep8()) {
      setErrors({});
      setStep(9);
    } else if (step === 9 && validateStep9()) {
      setErrors({});
      setStep(10);
    } else if (step === 10 && validateStep10()) {
      setErrors({});
      setStep(11);
    }
  };

  const handleBack = (e) => {
    e.preventDefault();
    setErrors({});
    if (step > 1) setStep(step - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleNext(e);
  };

  // Split pasal into two columns for display
  const middleIndex = Math.ceil(availablePasal.length / 2);
  const firstColumnPasal = availablePasal.slice(0, middleIndex);
  const secondColumnPasal = availablePasal.slice(middleIndex);

  return (
    <div className="flex justify-center mt-10 px-4">
      <form
        onSubmit={handleSubmit}
        className="bg-indigo-800 p-8 rounded-md max-w-md w-full space-y-6 text-indigo-900"
        noValidate
      >
        <h3 className="text-white text-center font-semibold text-xl mb-6">Laporan Polisi</h3>

        {/* Section titles for steps */}
        {(step === 1 || step === 2) && (
          <div
            className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6 -mt-1"
          >
            Yang Dilaporkan:
          </div>
        )}
        {(step === 3 || step === 4 || step === 5) && (
          <div
            className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6 -mt-1"
          >
            Peristiwa Yang diLaporkan
          </div>
        )}
        {step === 6 && (
          <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6">
            PASAL:
          </div>
        )}
        {step === 7 && (
          <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6">
            TINDAK PIDANA
          </div>
        )}
        {step === 8 && (
          <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6">
            NAMA DAN ALAMAT SAKSI - SAKSI
          </div>
        )}
        {step === 9 && (
          <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6">
            BARANG BUKTI
          </div>
        )}
        {step === 10 && (
          <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-4">
            URAIAN SINGKAT YANG DI LAPORKAN
          </div>
        )}
        {step === 11 && (
          <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6">
            PREVIEW SURAT LAPORAN POLISI
          </div>
        )}

        {/* Step 1 */}
        {step === 1 && (
          <>
            <div>
              <label className="block mb-1 text-white font-semibold" htmlFor="noLp">No Lp</label>
              <input
                id="noLp"
                type="text"
                name="noLp"
                value={formData.noLp}
                onChange={handleChange}
                placeholder="Wajib diisi"
                className={`w-full px-3 py-2 rounded ${errors.noLp ? "border border-red-500" : ""}`}
                autoComplete="off"
              />
              {errors.noLp && <p className="text-red-400 text-sm mt-1">{errors.noLp}</p>}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold" htmlFor="nama">Nama</label>
              <input
                id="nama"
                type="text"
                name="nama"
                value={formData.nama}
                onChange={e => {
                  e.target.value = e.target.value.toUpperCase();
                  handleChange(e);
                }}
                placeholder="Wajib diisi huruf kapital"
                className={`w-full px-3 py-2 rounded ${errors.nama ? "border border-red-500" : ""}`}
                autoComplete="off"
              />
              {errors.nama && <p className="text-red-400 text-sm mt-1">{errors.nama}</p>}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold" htmlFor="noIdentitas">No. Identitas</label>
              <input
                id="noIdentitas"
                type="text"
                name="noIdentitas"
                value={formData.noIdentitas}
                onChange={handleChange}
                placeholder="Wajib diisi"
                className={`w-full px-3 py-2 rounded ${errors.noIdentitas ? "border border-red-500" : ""}`}
                autoComplete="off"
              />
              {errors.noIdentitas && (
                <p className="text-red-400 text-sm mt-1">{errors.noIdentitas}</p>
              )}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold" htmlFor="kewarganegaraan">Kewarganegaraan</label>
              <select
                id="kewarganegaraan"
                name="kewarganegaraan"
                value={formData.kewarganegaraan}
                onChange={handleChange}
                className={`w-full px-3 py-2 rounded text-indigo-900 ${
                  errors.kewarganegaraan ? "border border-red-500" : ""
                }`}
              >
                <option value="">Pilih</option>
                <option value="WNI">WNI</option>
                <option value="WNA">WNA</option>
              </select>
              {errors.kewarganegaraan && (
                <p className="text-red-400 text-sm mt-1">{errors.kewarganegaraan}</p>
              )}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold" htmlFor="jenisKelamin">Jenis Kelamin</label>
              <select
                id="jenisKelamin"
                name="jenisKelamin"
                value={formData.jenisKelamin}
                onChange={handleChange}
                className={`w-full px-3 py-2 rounded text-indigo-900 ${
                  errors.jenisKelamin ? "border border-red-500" : ""
                }`}
              >
                <option value="">Pilih</option>
                <option value="Laki-Laki">Laki - Laki</option>
                <option value="Perempuan">Perempuan</option>
              </select>
              {errors.jenisKelamin && (
                <p className="text-red-400 text-sm mt-1">{errors.jenisKelamin}</p>
              )}
            </div>

            <button
              type="submit"
              className="w-full bg-pink-500 hover:bg-pink-600 text-white font-semibold rounded py-2"
            >
              Next
            </button>
          </>
        )}

        {/* Implement steps 2 - 11 similarly (omitted here for brevity) */}

        {/* Step 11 Preview */}
        {step === 11 && (
          <>
            <div
              id="surat-laporan-polisi-preview"
              className="bg-white p-6 text-black text-xs"
              style={{ fontFamily: "Times New Roman, serif", padding: "20px" }}
            >
              {/* Header */}
              <div className="flex justify-between items-start mb-4">
                <div className="text-left leading-tight" style={{ fontSize: "9px" }}>
                  <p className="font-bold">KEPOLISIAN NEGARA REPUBLIK INDONESIA</p>
                  <p className="font-bold">DAERAH METRO JAYA</p>
                  <p className="font-bold">SENTRA PELAYANAN KEPOLISIAN TERPADU</p>
                </div>
                <div className="text-right leading-tight" style={{ fontSize: "8px" }}>
                  <p className="font-bold">S.I.1.32</p>
                  <p>Model B</p>
                  <p className="font-bold mt-2">"PRO JUSTITIA"</p>
                </div>
              </div>

              <div className="flex justify-center my-4">
                <img
                  src="/images/polda_logo.png"
                  alt="Logo Polda Metro Jaya"
                  style={{ width: 64, height: 64, display: "block", margin: "0 auto" }}
                  onError={(e) => { e.target.onerror = null; e.target.src = "https://storage.googleapis.com/workspace-0f70711f-8b4e-4d94-86f1-2a93ccde5887/image/52c8ecb6-c3bf-4c40-8e2a-819d66a26d94.png"; }}
                />
              </div>

              <div className="text-center my-4" style={{ fontSize: "10px" }}>
                <p className="font-bold underline tracking-wide">LAPORAN POLISI</p>
                <p>Nomor: {formData.noLp || "LP/B/.../SPKT.PMJ"}</p>
              </div>

              {/* Yang Melapor */}
              <p className="font-bold mt-2 mb-1">YANG MELAPORKAN :</p>
              <table className="w-full ml-2 mb-4" style={{ fontSize: "8px" }}>
                <tbody>
                  <tr>
                    <td style={{ width: "30%" }}>1. Nama</td>
                    <td>: {formData.nama.toUpperCase() || "[...]"} </td>
                  </tr>
                  <tr>
                    <td>2. No Identitas</td>
                    <td>: {formData.noIdentitas || "[...]"} </td>
                  </tr>
                  <tr>
                    <td>3. Kewarganegaraan</td>
                    <td>: {formData.kewarganegaraan || "[...]"} </td>
                  </tr>
                  <tr>
                    <td>4. Jenis Kelamin</td>
                    <td>: {formData.jenisKelamin || "[...]"} </td>
                  </tr>
                  <tr>
                    <td>5. Tempat/Tgl Lahir</td>
                    <td>
                      : {formData.tempatLahir.toUpperCase() || "[...]"}, {formData.tanggalLahir || "[...]"}
                    </td>
                  </tr>
                  <tr>
                    <td>6. Pekerjaan</td>
                    <td>: {formData.pekerjaan.toUpperCase() || "[...]"} </td>
                  </tr>
                  <tr>
                    <td>7. Agama</td>
                    <td>: {formData.agama || "[...]"} </td>
                  </tr>
                  <tr>
                    <td>8. Alamat</td>
                    <td>: {formData.alamat || "[...]"} </td>
                  </tr>
                  <tr>
                    <td>9. Kontak</td>
                    <td>: {formData.noTelepon || "[...]"} </td>
                  </tr>
                </tbody>
              </table>

              {/* Peristiwa yang Dilaporkan */}
              <p className="font-bold mt-2 mb-1">PERISTIWA YANG DILAPORKAN :</p>
              <table className="w-full ml-2 mb-4" style={{ fontSize: "8px" }}>
                <tbody>
                  <tr>
                    <td style={{ width: "30%" }}>1. Waktu Kejadian</td>
                    <td>
                      : Pukul {formData.pukulJam || "00"}.{formData.pukulMenit || "00"} {formData.zonaWaktu || "WIB"}, Hari{" "}
                      {formData.hariKejadian || "[...]"}, Tanggal {formData.tanggalKejadian || "[...]"}.
                    </td>
                  </tr>
                  <tr>
                    <td>2. Tempat Kejadian</td>
                    <td>: {formData.tempatKejadian || "[...]"}.</td>
                  </tr>
                  <tr>
                    <td>3. Apa Yang Terjadi</td>
                    <td>: {formData.apaYangTerjadi || "[...]"}.</td>
                  </tr>
                  <tr>
                    <td>4. Siapa Terlapor</td>
                    <td>
                      :{" "}
                      {formData.siapaTerlapor === "Dalam Lidik"
                        ? "DALAM LIDIK"
                        : `${formData.terlaporNama.toUpperCase() || "[...]"} (No. Identitas: ${
                            formData.terlaporNoIdentitas || "[...]"
                          }, Kewarganegaraan: ${formData.terlaporKewarganegaraan || "[...]"}, Suku: ${
                            formData.terlaporSuku || "[...]"
                          }, Jenis Kelamin: ${formData.terlaporJenisKelamin || "[...]"}, Tempat/Tgl Lahir: ${
                            formData.terlaporTempatLahir || "[...]"
                          }, ${formData.terlaporTanggalLahir || "[...]"}.
                        Umur: ${formData.terlaporUmurTahun || "00"} Tahun ${formData.terlaporUmurBulan || "00"} Bulan ${
                            formData.terlaporUmurHari || "00"
                          } Hari, Pekerjaan: ${formData.terlaporPekerjaan || "[...]"}, Agama: ${
                            formData.terlaporAgama || "[...]"
                          }, Alamat: ${formData.terlaporAlamat || "[...]"}, No. Telp: ${
                            formData.terlaporNoTelp || "[...]"
                          }${formData.terlaporMediaSosial ? `, Akun Media Sosial: ${formData.terlaporMediaSosial}` : ""}${
                            formData.terlaporKorporasi ? " / KORPORASI" : ""
                          }).`}
                    </td>
                  </tr>
                  <tr>
                    <td>5. Siapa Korban</td>
                    <td>
                      : {formData.korbanNama.toUpperCase() || "[...]"} (No. Identitas: {formData.korbanNoIdentitas || "[...]"},
                      Kewarganegaraan: {formData.korbanKewarganegaraan || "[...]"}, Suku: {formData.korbanSuku || "[...]"},
                      Jenis Kelamin: {formData.korbanJenisKelamin || "[...]"}, Tempat/Tgl Lahir: {formData.korbanTempatLahir || "[...]"},
                      {formData.korbanTanggalLahir || "[...]"}.
                      Umur: {formData.korbanUmurTahun || "00"} Tahun {formData.korbanUmurBulan || "00"} Bulan{" "}
                      {formData.korbanUmurHari || "00"} Hari, Pekerjaan: {formData.korbanPekerjaan || "[...]"}, Agama:{" "}
                      {formData.korbanAgama || "[...]"}, Alamat: {formData.korbanAlamat || "[...]"}, No. Telp:{" "}
                      {formData.korbanNoTelp || "[...]"}
                      {formData.korbanMediaSosial ? `, Akun Media Sosial: ${formData.korbanMediaSosial}` : ""}
                      {formData.korbanKorporasi ? " / KORPORASI" : ""}).
                    </td>
                  </tr>
                  <tr>
                    <td>6. Kapan Dilaporkan</td>
                    <td>
                      : Hari {formData.kapanDilaporkanHari || "[...]"}, Tanggal {formData.kapanDilaporkanTanggal || "[...]"}.
                      Pukul {formData.kapanDilaporkanJam || "00"}.{formData.kapanDilaporkanMenit || "00"}{" "}
                      {formData.kapanDilaporkanZonaWaktu || "WIB"}.
                    </td>
                  </tr>
                </tbody>
              </table>

              <table className="w-full border-collapse border border-black text-xs mt-4" style={{ fontSize: "8px" }}>
                <thead>
                  <tr className="bg-gray-200 text-center font-bold">
                    <td className="border border-black p-1 w-1/2">TINDAK PIDANA APA:</td>
                    <td className="border border-black p-1 w-1/2">NAMA DAN ALAMAT SAKSI - SAKSI:</td>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td className="border border-black p-2 align-top whitespace-pre-wrap">{formData.tindakPidana}</td>
                    <td className="border border-black p-2 align-top whitespace-normal">
                      {formData.saksi.length > 0 ? (
                        formData.saksi.map((saksi, index) => (
                          <p
                            key={index}
                            style={{ fontSize: "7px", marginBottom: "2px", wordBreak: "break-word" }}
                          >
                            {index + 1}. Nama/Alias: {saksi.nama.toUpperCase() || "[...]"}, No. Identitas:{" "}
                            {saksi.noIdentitas || "[...]"}, Kewarganegaraan: {saksi.kewarganegaraan || "[...]"}, Suku:{" "}
                            {saksi.suku || "[...]"}, Jenis Kelamin: {saksi.jenisKelamin || "[...]"}, Tempat/Tgl Lahir:{" "}
                            {saksi.tempatLahir.toUpperCase() || "[...]"}, {saksi.tanggalLahir || "[...]"}.
                            Umur: {saksi.umurTahun || "00"} Tahun {saksi.umurBulan || "00"} Bulan {saksi.umurHari || "00"} Hari, Pekerjaan:{" "}
                            {saksi.pekerjaan.toUpperCase() || "[...]"}, Agama: {saksi.agama || "[...]"}, Alamat:{" "}
                            {saksi.alamat || "[...]"}, No. Telp: {saksi.noTelp || "[...]"}, Akun Media Sosial:{" "}
                            {saksi.mediaSosial || "[...]"}.{" "}
                          </p>
                        ))
                      ) : (
                        <p>Tidak ada saksi.</p>
                      )}
                    </td>
                  </tr>
                  <tr className="bg-gray-200 text-center font-bold" style={{ fontSize: "8px" }}>
                    <td className="border border-black p-1">BARANG BUKTI:</td>
                    <td className="border border-black p-1">URAIAN SINGKAT YANG DILAPORKAN:</td>
                  </tr>
                  <tr>
                    <td className="border border-black p-2 align-top whitespace-pre-wrap">{formData.barangBukti}</td>
                    <td className="border border-black p-2 align-top whitespace-pre-wrap">{formData.uraianSingkat}</td>
                  </tr>
                </tbody>
              </table>

              <p className="mt-4 text-xs">
                Pelapor atau pengadu memberikan keterangan, kemudian membubuhkan tandatangannya di bawah ini.
              </p>
              <div className="flex justify-end mt-4">
                <div className="text-center">
                  <p className="font-bold">Pelapor</p>
                  <p className="mt-8">{formData.isAnonymous ? "ANONYMOUS" : formData.nama.toUpperCase()}</p>
                </div>
              </div>

              <p className="font-bold mt-4 text-xs">TINDAKAN YANG DILAKUKAN :</p>
              <ol className="list-decimal list-inside ml-4 text-xs" style={{ fontSize: "8px" }}>
                <li>Membuat Laporan Polisi</li>
                <li>Membuat tanda bukti lapor.</li>
                <li>Menerima barang bukti.</li>
              </ol>

              <div className="flex justify-between items-start mt-8 text-xs" style={{ fontSize: "8px" }}>
                <div>
                  <p>Piket Siaga</p>
                  <p className="mt-8">Nama</p>
                  <p>PANGKAT NRP 0000000</p>
                </div>
                <div className="text-right">
                  <p>
                    Jakarta,{" "}
                    {new Date().toLocaleDateString("id-ID", {
                      day: "2-digit",
                      month: "long",
                      year: "numeric",
                    })}
                  </p>
                  <p className="font-bold">Yang Menerima Laporan</p>
                  <p className="font-bold">Penyelidik / Penyidik</p>
                  <p className="mt-8">Nama</p>
                  <p>PANGKAT NRP 0000000</p>
                </div>
              </div>
            </div>

            <div className="flex justify-between space-x-4 mt-8">
              <button
                type="button"
                onClick={handleBack}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              <button
                type="button"
                onClick={handleDownloadPDF}
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded py-2"
              >
                Download Laporan
              </button>
            </div>
          </>
        )}
      </form>
    </div>
  );
}
