            {formData.siapaTerlapor === "Terlapor" && (
              <>
                {/* Terlapor fields */}
                {/* ... (sama dengan kode anda sebelumnya di step 4) */}
                <div>
                  <label className="block mb-1 text-white font-semibold">Nama / Alias</label>
                  <input
