import React, { useState } from "react";

export default function PenyelidikanForm() {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    noLp: "",
    nama: "",
    noIdentitas: "",
    kewarganegaraan: "",
    jenisKelamin: "",
    tempatLahir: "",
    tanggalLahir: "",
    pekerjaan: "",
    agama: "",
    alamat: "",
    noTelepon: "",
    hariKejadian: "",
    tanggalKejadian: "",
    pukulJam: "",
    pukulMenit: "",
    zonaWaktu: "WIB",
    tempatKejadian: "",
    apaYangTerjadi: "",
    siapaTerlapor: "",
    terlaporNama: "",
    terlaporNoIdentitas: "",
    terlaporKewarganegaraan: "",
    terlaporSuku: "",
    terlaporJenisKelamin: "",
    terlaporTempatLahir: "",
    terlaporTanggalLahir: "",
    terlaporUmurTahun: "",
    terlaporUmurBulan: "",
    terlaporUmurHari: "",
    terlaporPekerjaan: "",
    terlaporAgama: "",
    terlaporAlamat: "",
    terlaporNoTelp: "",
    terlaporMediaSosial: "",
    terlaporKorporasi: false,
  });

  const [errors, setErrors] = useState({});

  // Validasi tiap step
  const validateStep1 = () => {
    const newErrors = {};
    if (!formData.noLp) newErrors.noLp = "Nomor LP wajib diisi";
    if (!formData.nama) newErrors.nama = "Nama wajib diisi";
    if (!formData.noIdentitas) newErrors.noIdentitas = "No. Identitas wajib diisi";
    if (!formData.kewarganegaraan) newErrors.kewarganegaraan = "Pilih kewarganegaraan";
    if (!formData.jenisKelamin) newErrors.jenisKelamin = "Pilih jenis kelamin";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep2 = () => {
    const newErrors = {};
    if (!formData.tempatLahir) newErrors.tempatLahir = "Tempat lahir wajib diisi";
    if (!formData.tanggalLahir) newErrors.tanggalLahir = "Tanggal lahir wajib diisi";
    if (!formData.pekerjaan) newErrors.pekerjaan = "Pekerjaan wajib diisi";
    if (!formData.agama) newErrors.agama = "Agama wajib dipilih";
    if (!formData.alamat) newErrors.alamat = "Alamat wajib diisi";
    if (!formData.noTelepon) newErrors.noTelepon = "No. telepon wajib diisi";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep3 = () => {
    const newErrors = {};
    if (!formData.hariKejadian) newErrors.hariKejadian = "Hari kejadian wajib diisi";
    if (!formData.tanggalKejadian) newErrors.tanggalKejadian = "Tanggal kejadian wajib diisi";
    if (!formData.pukulJam) newErrors.pukulJam = "Jam wajib diisi";
    if (!formData.pukulMenit) newErrors.pukulMenit = "Menit wajib dipilih";
    if (!formData.zonaWaktu) newErrors.zonaWaktu = "Zona waktu wajib dipilih";
    if (!formData.tempatKejadian) newErrors.tempatKejadian = "Tempat kejadian wajib diisi";
    if (!formData.apaYangTerjadi) newErrors.apaYangTerjadi = "Harap jelaskan apa yang terjadi";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep4 = () => {
    const newErrors = {};
    if (!formData.siapaTerlapor) newErrors.siapaTerlapor = "Pilih siapa yang terlapor";
    if (formData.siapaTerlapor === "Terlapor") {
      if (!formData.terlaporNama) newErrors.terlaporNama = "Nama/Alias wajib diisi";
      if (!formData.terlaporNoIdentitas) newErrors.terlaporNoIdentitas = "No. Identitas wajib diisi";
      if (!formData.terlaporKewarganegaraan) newErrors.terlaporKewarganegaraan = "Pilih kewarganegaraan";
      if (!formData.terlaporSuku) newErrors.terlaporSuku = "Suku wajib diisi";
      if (!formData.terlaporJenisKelamin) newErrors.terlaporJenisKelamin = "Pilih jenis kelamin";
      if (!formData.terlaporTempatLahir) newErrors.terlaporTempatLahir = "Tempat lahir wajib diisi";
      if (!formData.terlaporTanggalLahir) newErrors.terlaporTanggalLahir = "Tanggal lahir wajib diisi";
      if (!formData.terlaporUmurTahun) newErrors.terlaporUmurTahun = "Umur Tahun wajib diisi";
      if (!formData.terlaporPekerjaan) newErrors.terlaporPekerjaan = "Pekerjaan wajib diisi";
      if (!formData.terlaporAgama) newErrors.terlaporAgama = "Pilih agama";
      if (!formData.terlaporAlamat) newErrors.terlaporAlamat = "Alamat wajib diisi";
      if (!formData.terlaporNoTelp) newErrors.terlaporNoTelp = "No. Telp wajib diisi";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleNext = (e) => {
    e.preventDefault();
    if (step === 1 && validateStep1()) {
      setErrors({});
      setStep(2);
    } else if (step === 2 && validateStep2()) {
      setErrors({});
      setStep(3);
    } else if (step === 3 && validateStep3()) {
      setErrors({});
      setStep(4);
    }
  };

  const handleBack = (e) => {
    e.preventDefault();
    setErrors({});
    if (step > 1) setStep(step - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (step === 4 && validateStep4()) {
      setErrors({});
      alert("Data berhasil disimpan!");
      // Lakukan submit data ke backend disini
    }
  };

  return (
    <div className="flex justify-center mt-10">
      <form
        onSubmit={step < 4 ? handleNext : handleSubmit}
        className="bg-indigo-800 p-8 rounded-md max-w-md w-full space-y-6 text-indigo-900"
      >
        <h3 className="text-white text-center font-semibold text-xl mb-6">
          Laporan Polisi
        </h3>

        {(step === 1 || step === 2) && (
          <div
            className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6"
            style={{ marginTop: "-0.25rem" }}
          >
            Yang Dilaporkan:
          </div>
        )}

        {step === 3 && (
          <div
            className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6"
            style={{ marginTop: "-0.25rem" }}
          >
            Peristiwa Yang diLaporkan
          </div>
        )}

        {step === 4 && (
          <div
            className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6"
            style={{ marginTop: "-0.25rem" }}
          >
            Siapa Yang Terlapor
          </div>
        )}

        {/* Step 1 */}
        {step === 1 && (
          <>
            <div>
              <label className="block mb-1 text-white font-semibold">No Lp</label>
              <input
                type="text"
                name="noLp"
                value={formData.noLp}
                onChange={handleChange}
                placeholder="Wajib diisi"
                className={`w-full px-3 py-2 rounded ${
                  errors.noLp ? "border border-red-500" : ""
                }`}
              />
              {errors.noLp && <p className="text-red-400 text-sm mt-1">{errors.noLp}</p>}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Nama</label>
              <input
                type="text"
                name="nama"
                value={formData.nama}
                onChange={handleChange}
                placeholder="Wajib diisi huruf kapital"
                style={{ textTransform: "uppercase" }}
                className={`w-full px-3 py-2 rounded ${
                  errors.nama ? "border border-red-500" : ""
                }`}
              />
              {errors.nama && <p className="text-red-400 text-sm mt-1">{errors.nama}</p>}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">No. Identitas</label>
              <input
                type="text"
                name="noIdentitas"
                value={formData.noIdentitas}
                onChange={handleChange}
                placeholder="Wajib diisi"
                className={`w-full px-3 py-2 rounded ${
                  errors.noIdentitas ? "border border-red-500" : ""
                }`}
              />
              {errors.noIdentitas && (
                <p className="text-red-400 text-sm mt-1">{errors.noIdentitas}</p>
              )}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Kewarganegaraan</label>
              <select
                name="kewarganegaraan"
                value={formData.kewarganegaraan}
                onChange={handleChange}
                className={`w-full px-3 py-2 rounded text-indigo-900 ${
                  errors.kewarganegaraan ? "border border-red-500" : ""
                }`}
              >
                <option value="">Pilih</option>
                <option value="WNI">WNI</option>
                <option value="WNA">WNA</option>
              </select>
              {errors.kewarganegaraan && (
                <p className="text-red-400 text-sm mt-1">{errors.kewarganegaraan}</p>
              )}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Jenis Kelamin</label>
              <select
                name="jenisKelamin"
                value={formData.jenisKelamin}
                onChange={handleChange}
                className={`w-full px-3 py-2 rounded text-indigo-900 ${
                  errors.jenisKelamin ? "border border-red-500" : ""
                }`}
              >
                <option value="">Pilih</option>
                <option value="Laki-Laki">Laki - Laki</option>
                <option value="Perempuan">Perempuan</option>
              </select>
              {errors.jenisKelamin && (
                <p className="text-red-400 text-sm mt-1">{errors.jenisKelamin}</p>
              )}
            </div>

            <button
              type="submit"
              className="w-full bg-pink-500 hover:bg-pink-600 text-white font-semibold rounded py-2"
            >
              Next
            </button>
          </>
        )}

        {/* Step 2 */}
        {step === 2 && (
          <>
            <div className="flex space-x-4">
              <div className="flex-1">
                <label className="block mb-1 text-white font-semibold">Tempat Lahir</label>
                <input
                  type="text"
                  name="tempatLahir"
                  value={formData.tempatLahir}
                  onChange={handleChange}
                  placeholder="Masukkan tempat lahir"
                  className={`w-full px-3 py-2 rounded ${
                    errors.tempatLahir ? "border border-red-500" : ""
                  }`}
                />
                {errors.tempatLahir && (
                  <p className="text-red-400 text-sm mt-1">{errors.tempatLahir}</p>
                )}
              </div>

              <div className="flex-1">
                <label className="block mb-1 text-white font-semibold">Tanggal Lahir</label>
                <input
                  type="date"
                  name="tanggalLahir"
                  value={formData.tanggalLahir}
                  onChange={handleChange}
                  className={`w-full px-3 py-2 rounded ${
                    errors.tanggalLahir ? "border border-red-500" : ""
                  }`}
                />
                {errors.tanggalLahir && (
                  <p className="text-red-400 text-sm mt-1">{errors.tanggalLahir}</p>
                )}
              </div>
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Pekerjaan</label>
              <input
                type="text"
                name="pekerjaan"
                value={formData.pekerjaan}
                onChange={handleChange}
                placeholder="Masukkan pekerjaan"
                className={`w-full px-3 py-2 rounded ${
                  errors.pekerjaan ? "border border-red-500" : ""
                }`}
              />
              {errors.pekerjaan && (
                <p className="text-red-400 text-sm mt-1">{errors.pekerjaan}</p>
              )}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Agama</label>
              <select
                name="agama"
                value={formData.agama}
                onChange={handleChange}
                className={`w-full px-3 py-2 rounded text-indigo-900 ${
                  errors.agama ? "border border-red-500" : ""
                }`}
              >
                <option value="">Pilih</option>
                <option value="Islam">Islam</option>
                <option value="Katolik">Katolik</option>
                <option value="Protestan">Protestan</option>
                <option value="Hindu">Hindu</option>
                <option value="Budha">Budha</option>
              </select>
              {errors.agama && (
                <p className="text-red-400 text-sm mt-1">{errors.agama}</p>
              )}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Alamat</label>
              <textarea
                name="alamat"
                value={formData.alamat}
                onChange={handleChange}
                placeholder="Masukkan alamat lengkap"
                rows={3}
                className={`w-full px-3 py-2 rounded resize-none ${
                  errors.alamat ? "border border-red-500" : ""
                }`}
              />
              {errors.alamat && (
                <p className="text-red-400 text-sm mt-1">{errors.alamat}</p>
              )}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">No. Telepon</label>
              <input
                type="text"
                name="noTelepon"
                value={formData.noTelepon}
                onChange={handleChange}
                placeholder="Masukkan nomor telepon"
                className={`w-full px-3 py-2 rounded ${
                  errors.noTelepon ? "border border-red-500" : ""
                }`}
              />
              {errors.noTelepon && (
                <p className="text-red-400 text-sm mt-1">{errors.noTelepon}</p>
              )}
            </div>

            <div className="flex justify-between space-x-4">
              <button
                onClick={handleBack}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              <button
                type="submit"
                className="flex-1 bg-pink-500 hover:bg-pink-600 text-white font-semibold rounded py-2"
              >
                Next
              </button>
            </div>
          </>
        )}

        {/* Step 3 */}
        {step === 3 && (
          <>
            <div className="flex space-x-4 mb-4">
              <div className="flex-1">
                <label className="block mb-1 text-white font-semibold">Hari Kejadian</label>
                <input
                  type="text"
                  name="hariKejadian"
                  value={formData.hariKejadian}
                  onChange={handleChange}
                  placeholder="Masukkan hari kejadian"
                  className={`w-full px-3 py-2 rounded ${
                    errors.hariKejadian ? "border border-red-500" : ""
                  }`}
                />
                {errors.hariKejadian && (
                  <p className="text-red-400 text-sm mt-1">{errors.hariKejadian}</p>
                )}
              </div>

              <div className="flex-1">
                <label className="block mb-1 text-white font-semibold">Tanggal Kejadian</label>
                <input
                  type="date"
                  name="tanggalKejadian"
                  value={formData.tanggalKejadian}
                  onChange={handleChange}
                  className={`w-full px-3 py-2 rounded ${
                    errors.tanggalKejadian ? "border border-red-500" : ""
                  }`}
                />
                {errors.tanggalKejadian && (
                  <p className="text-red-400 text-sm mt-1">{errors.tanggalKejadian}</p>
                )}
              </div>
            </div>

            <div className="flex space-x-4 mb-4">
              <div className="flex-1">
                <label className="block mb-1 text-white font-semibold">Pukul (Jam)</label>
                <input
                  type="number"
                  name="pukulJam"
                  value={formData.pukulJam}
                  onChange={handleChange}
                  min="0"
                  max="23"
                  placeholder="00 - 23"
                  className={`w-full px-3 py-2 rounded ${
                    errors.pukulJam ? "border border-red-500" : ""
                  }`}
                />
                {errors.pukulJam && (
                  <p className="text-red-400 text-sm mt-1">{errors.pukulJam}</p>
                )}
              </div>

              <div className="flex-1">
                <label className="block mb-1 text-white font-semibold">Pukul (Menit)</label>
                <select
                  name="pukulMenit"
                  value={formData.pukulMenit}
                  onChange={handleChange}
                  className={`w-full px-3 py-2 rounded ${
                    errors.pukulMenit ? "border border-red-500" : ""
                  }`}
                >
                  <option value="">Pilih menit</option>
                  <option value="00">00</option>
                  <option value="30">30</option>
                </select>
                {errors.pukulMenit && (
                  <p className="text-red-400 text-sm mt-1">{errors.pukulMenit}</p>
                )}
              </div>

              <div className="flex-1">
                <label className="block mb-1 text-white font-semibold">Zona Waktu</label>
                <select
                  name="zonaWaktu"
                  value={formData.zonaWaktu}
                  onChange={handleChange}
                  className={`w-full px-3 py-2 rounded ${
                    errors.zonaWaktu ? "border border-red-500" : ""
                  }`}
                >
                  <option value="WIB">WIB</option>
                  <option value="WITA">WITA</option>
                  <option value="WIT">WIT</option>
                </select>
                {errors.zonaWaktu && (
                  <p className="text-red-400 text-sm mt-1">{errors.zonaWaktu}</p>
                )}
              </div>
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Tempat Kejadian</label>
              <input
                type="text"
                name="tempatKejadian"
                value={formData.tempatKejadian}
                onChange={handleChange}
                placeholder="Nama jalan, kec, kab/kota, provinsi"
                className={`w-full px-3 py-2 rounded ${
                  errors.tempatKejadian ? "border border-red-500" : ""
                }`}
              />
              {errors.tempatKejadian && (
                <p className="text-red-400 text-sm mt-1">{errors.tempatKejadian}</p>
              )}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Apa yang terjadi</label>
              <textarea
                name="apaYangTerjadi"
                value={formData.apaYangTerjadi}
                onChange={handleChange}
                placeholder="Jelaskan Apa yang Terjadi"
                rows={4}
                className={`w-full px-3 py-2 rounded resize-none ${
                  errors.apaYangTerjadi ? "border border-red-500" : ""
                }`}
              />
              {errors.apaYangTerjadi && (
                <p className="text-red-400 text-sm mt-1">{errors.apaYangTerjadi}</p>
              )}
            </div>

            <div className="flex justify-between space-x-4">
              <button
                onClick={handleBack}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              <button
                type="submit"
                className="flex-1 bg-pink-500 hover:bg-pink-600 text-white font-semibold rounded py-2"
              >
                Next
              </button>
            </div>
          </>
        )}

        {/* Step 4 */}
        {step === 4 && (
          <>
            <div>
              <label className="block mb-1 text-white font-semibold">Siapa yang Terlapor</label>
              <select
                name="siapaTerlapor"
                value={formData.siapaTerlapor}
                onChange={handleChange}
                className={`w-full px-3 py-2 rounded text-indigo-900 ${
                  errors.siapaTerlapor ? "border border-red-500" : ""
                }`}
              >
                <option value="">Pilih</option>
                <option value="Dalam Lidik">Dalam Lidik</option>
                <option value="Terlapor">Terlapor</option>
              </select>
              {errors.siapaTerlapor && (
                <p className="text-red-400 text-sm mt-1">{errors.siapaTerlapor}</p>
              )}
            </div>

            {formData.siapaTerlapor === "Terlapor" && (
              <>
                <div>
                  <label className="block mb-1 text-white font-semibold">Nama / Alias</label>
                  <input
                    type="text"
                    name="terlaporNama"
                    value={formData.terlaporNama}
                    onChange={handleChange}
                    placeholder="Nama atau alias"
                    className={`w-full px-3 py-2 rounded ${
                      errors.terlaporNama ? "border border-red-500" : ""
                    }`}
                  />
                  {errors.terlaporNama && (
                    <p className="text-red-400 text-sm mt-1">{errors.terlaporNama}</p>
                  )}
                </div>

                <div>
                  <label className="block mb-1 text-white font-semibold">No. Identitas</label>
                  <input
                    type="text"
                    name="terlaporNoIdentitas"
                    value={formData.terlaporNoIdentitas}
                    onChange={handleChange}
                    placeholder="No. Identitas"
                    className={`w-full px-3 py-2 rounded ${
                      errors.terlaporNoIdentitas ? "border border-red-500" : ""
                    }`}
                  />
                  {errors.terlaporNoIdentitas && (
                    <p className="text-red-400 text-sm mt-1">{errors.terlaporNoIdentitas}</p>
                  )}
                </div>

                <div>
                  <label className="block mb-1 text-white font-semibold">Kewarganegaraan</label>
                  <select
                    name="terlaporKewarganegaraan"
                    value={formData.terlaporKewarganegaraan}
                    onChange={handleChange}
                    className={`w-full px-3 py-2 rounded text-indigo-900 ${
                      errors.terlaporKewarganegaraan ? "border border-red-500" : ""
                    }`}
                  >
                    <option value="">Pilih</option>
                    <option value="WNI">WNI</option>
                    <option value="WNA">WNA</option>
                  </select>
                  {errors.terlaporKewarganegaraan && (
                    <p className="text-red-400 text-sm mt-1">{errors.terlaporKewarganegaraan}</p>
                  )}
                </div>

                <div>
                  <label className="block mb-1 text-white font-semibold">Suku</label>
                  <input
                    type="text"
                    name="terlaporSuku"
                    value={formData.terlaporSuku}
                    onChange={handleChange}
                    placeholder="Suku"
                    className={`w-full px-3 py-2 rounded ${
                      errors.terlaporSuku ? "border border-red-500" : ""
                    }`}
                  />
                  {errors.terlaporSuku && (
                    <p className="text-red-400 text-sm mt-1">{errors.terlaporSuku}</p>
                  )}
                </div>

                <div>
                  <label className="block mb-1 text-white font-semibold">Jenis Kelamin</label>
                  <select
                    name="terlaporJenisKelamin"
                    value={formData.terlaporJenisKelamin}
                    onChange={handleChange}
                    className={`w-full px-3 py-2 rounded text-indigo-900 ${
                      errors.terlaporJenisKelamin ? "border border-red-500" : ""
                    }`}
                  >
                    <option value="">Pilih</option>
                    <option value="Laki-Laki">Laki - Laki</option>
                    <option value="Perempuan">Perempuan</option>
                  </select>
                  {errors.terlaporJenisKelamin && (
                    <p className="text-red-400 text-sm mt-1">{errors.terlaporJenisKelamin}</p>
                  )}
                </div>

                <div className="flex space-x-4">
                  <div className="flex-1">
                    <label className="block mb-1 text-white font-semibold">Tempat Lahir</label>
                    <input
                      type="text"
                      name="terlaporTempatLahir"
                      value={formData.terlaporTempatLahir}
                      onChange={handleChange}
                      placeholder="Tempat lahir"
                      className={`w-full px-3 py-2 rounded ${
                        errors.terlaporTempatLahir ? "border border-red-500" : ""
                      }`}
                    />
                    {errors.terlaporTempatLahir && (
                      <p className="text-red-400 text-sm mt-1">{errors.terlaporTempatLahir}</p>
                    )}
                  </div>

                  <div className="flex-1">
                    <label className="block mb-1 text-white font-semibold">Tanggal Lahir</label>
                    <input
                      type="date"
                      name="terlaporTanggalLahir"
                      value={formData.terlaporTanggalLahir}
                      onChange={handleChange}
                      className={`w-full px-3 py-2 rounded ${
                        errors.terlaporTanggalLahir ? "border border-red-500" : ""
                      }`}
                    />
                    {errors.terlaporTanggalLahir && (
                      <p className="text-red-400 text-sm mt-1">{errors.terlaporTanggalLahir}</p>
                    )}
                  </div>
                </div>

                <div className="flex space-x-4">
                  <div className="flex-1">
                    <label className="block mb-1 text-white font-semibold">Umur Tahun</label>
                    <input
                      type="number"
                      name="terlaporUmurTahun"
                      value={formData.terlaporUmurTahun}
                      onChange={handleChange}
                      min="0"
                      placeholder="Tahun"
                      className={`w-full px-3 py-2 rounded ${
                        errors.terlaporUmurTahun ? "border border-red-500" : ""
                      }`}
                    />
                    {errors.terlaporUmurTahun && (
                      <p className="text-red-400 text-sm mt-1">{errors.terlaporUmurTahun}</p>
                    )}
                  </div>
                  <div className="flex-1">
                    <label className="block mb-1 text-white font-semibold">Umur Bulan</label>
                    <input
                      type="number"
                      name="terlaporUmurBulan"
                      value={formData.terlaporUmurBulan}
                      onChange={handleChange}
                      min="0"
                      max="11"
                      placeholder="Bulan"
                      className="w-full px-3 py-2 rounded"
                    />
                  </div>
                  <div className="flex-1">
                    <label className="block mb-1 text-white font-semibold">Umur Hari</label>
                    <input
                      type="number"
                      name="terlaporUmurHari"
                      value={formData.terlaporUmurHari}
                      onChange={handleChange}
                      min="0"
                      max="30"
                      placeholder="Hari"
                      className="w-full px-3 py-2 rounded"
                    />
                  </div>
                </div>

                <div>
                  <label className="block mb-1 text-white font-semibold">Pekerjaan</label>
                  <input
                    type="text"
                    name="terlaporPekerjaan"
                    value={formData.terlaporPekerjaan}
                    onChange={handleChange}
                    placeholder="Pekerjaan"
                    className={`w-full px-3 py-2 rounded ${
                      errors.terlaporPekerjaan ? "border border-red-500" : ""
                    }`}
                  />
                  {errors.terlaporPekerjaan && (
                    <p className="text-red-400 text-sm mt-1">{errors.terlaporPekerjaan}</p>
                  )}
                </div>

                <div>
                  <label className="block mb-1 text-white font-semibold">Agama</label>
                  <select
                    name="terlaporAgama"
                    value={formData.terlaporAgama}
                    onChange={handleChange}
                    className={`w-full px-3 py-2 rounded text-indigo-900 ${
                      errors.terlaporAgama ? "border border-red-500" : ""
                    }`}
                  >
                    <option value="">Pilih</option>
                    <option value="Islam">Islam</option>
                    <option value="Katolik">Katolik</option>
                    <option value="Protestan">Protestan</option>
                    <option value="Hindu">Hindu</option>
                    <option value="Budha">Budha</option>
                  </select>
                  {errors.terlaporAgama && (
                    <p className="text-red-400 text-sm mt-1">{errors.terlaporAgama}</p>
                  )}
                </div>

                <div>
                  <label className="block mb-1 text-white font-semibold">Alamat</label>
                  <textarea
                    name="terlaporAlamat"
                    value={formData.terlaporAlamat}
                    onChange={handleChange}
                    placeholder="Alamat lengkap"
                    rows={3}
                    className={`w-full px-3 py-2 rounded resize-none ${
                      errors.terlaporAlamat ? "border border-red-500" : ""
                    }`}
                  />
                  {errors.terlaporAlamat && (
                    <p className="text-red-400 text-sm mt-1">{errors.terlaporAlamat}</p>
                  )}
                </div>

                <div>
                  <label className="block mb-1 text-white font-semibold">No. Telepon</label>
                  <input
                    type="text"
                    name="terlaporNoTelp"
                    value={formData.terlaporNoTelp}
                    onChange={handleChange}
                    placeholder="No. telepon"
                    className={`w-full px-3 py-2 rounded ${
                      errors.terlaporNoTelp ? "border border-red-500" : ""
                    }`}
                  />
                  {errors.terlaporNoTelp && (
                    <p className="text-red-400 text-sm mt-1">{errors.terlaporNoTelp}</p>
                  )}
                </div>

                <div>
                  <label className="block mb-1 text-white font-semibold">Akun Media Sosial (Platform)</label>
                  <input
                    type="text"
                    name="terlaporMediaSosial"
                    value={formData.terlaporMediaSosial}
                    onChange={handleChange}
                    placeholder="Contoh: Instagram, Facebook, Twitter"
                    className="w-full px-3 py-2 rounded"
                  />
                </div>

                <div className="flex items-center space-x-2 mt-4">
                  <input
                    type="checkbox"
                    id="korporasi"
                    name="terlaporKorporasi"
                    checked={formData.terlaporKorporasi}
                    onChange={handleChange}
                    className="rounded"
                  />
                  <label htmlFor="korporasi" className="text-white font-semibold">
                    KORPORASI
                  </label>
                </div>
              </>
            )}

            <div className="flex justify-between space-x-4 mt-6">
              <button
                onClick={handleBack}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              <button
                type="submit"
                className="flex-1 bg-pink-500 hover:bg-pink-600 text-white font-semibold rounded py-2"
              >
                Submit
              </button>
            </div>
          </>
        )}
      </form>
    </div>
  );
}
