import React, { useState } from "react";

export default function PenyelidikanForm() {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    noLp: "",
    nama: "",
    noIdentitas: "",
    kewarganegaraan: "",
    jenisKelamin: "",
    tempatLahir: "",
    tanggalLahir: "",
    pekerjaan: "",
    agama: "",
    alamat: "",
    noTelepon: "",
    hariKejadian: "",
    tanggalKejadian: "",
    pukulJam: "",
    pukulMenit: "",
    zonaWaktu: "WIB",
    tempatKejadian: "",
    apaYangTerjadi: "",
  });

  const [errors, setErrors] = useState({});

  const validateStep1 = () => {
    const newErrors = {};
    if (!formData.noLp) newErrors.noLp = "Nomor LP wajib diisi";
    if (!formData.nama) newErrors.nama = "Nama wajib diisi";
    if (!formData.noIdentitas) newErrors.noIdentitas = "No. Identitas wajib diisi";
    if (!formData.kewarganegaraan) newErrors.kewarganegaraan = "Pilih kewarganegaraan";
    if (!formData.jenisKelamin) newErrors.jenisKelamin = "Pilih jenis kelamin";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep2 = () => {
    const newErrors = {};
    if (!formData.tempatLahir) newErrors.tempatLahir = "Tempat lahir wajib diisi";
    if (!formData.tanggalLahir) newErrors.tanggalLahir = "Tanggal lahir wajib diisi";
    if (!formData.pekerjaan) newErrors.pekerjaan = "Pekerjaan wajib diisi";
    if (!formData.agama) newErrors.agama = "Agama wajib dipilih";
    if (!formData.alamat) newErrors.alamat = "Alamat wajib diisi";
    if (!formData.noTelepon) newErrors.noTelepon = "No. telepon wajib diisi";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep3 = () => {
    const newErrors = {};
    if (!formData.hariKejadian) newErrors.hariKejadian = "Hari kejadian wajib diisi";
    if (!formData.tanggalKejadian) newErrors.tanggalKejadian = "Tanggal kejadian wajib diisi";
    if (!formData.pukulJam) newErrors.pukulJam = "Jam wajib diisi";
    if (!formData.pukulMenit) newErrors.pukulMenit = "Menit wajib dipilih";
    if (!formData.zonaWaktu) newErrors.zonaWaktu = "Zona waktu wajib dipilih";
    if (!formData.tempatKejadian) newErrors.tempatKejadian = "Tempat kejadian wajib diisi";
    if (!formData.apaYangTerjadi) newErrors.apaYangTerjadi = "Harap jelaskan apa yang terjadi";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleNext = (e) => {
    e.preventDefault();
    if (step === 1 && validateStep1()) {
      setErrors({});
      setStep(2);
    } else if (step === 2 && validateStep2()) {
      setErrors({});
      setStep(3);
    }
  };

  const handleBack = (e) => {
    e.preventDefault();
    setErrors({});
    if (step > 1) setStep(step - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (step === 3 && validateStep3()) {
      setErrors({});
      alert("Data berhasil disimpan!");
      // Simpan data ke backend disini
    }
  };

  return (
    <div className="flex justify-center mt-10">
      <form
        onSubmit={step < 3 ? handleNext : handleSubmit}
        className="bg-indigo-800 p-8 rounded-md max-w-md w-full space-y-6 text-indigo-900"
      >
        <h3 className="text-white text-center font-semibold text-xl mb-6">
          Laporan Polisi
        </h3>

        {(step === 1 || step === 2) && (
          <div
            className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6"
            style={{ marginTop: "-0.25rem" }}
          >
            Yang Dilaporkan:
          </div>
        )}

        {step === 3 && (
          <div
            className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6"
            style={{ marginTop: "-0.25rem" }}
          >
            Peristiwa Yang diLaporkan
          </div>
        )}

        {step === 1 && (
          <>
            {/* Step 1 inputs */}
            {/* ... sama seperti sebelumnya */}
            {/* contoh input No Lp */}
            <div>
              <label className="block mb-1 text-white font-semibold">No Lp</label>
              <input
                type="text"
                name="noLp"
                value={formData.noLp}
                onChange={handleChange}
                placeholder="Wajib diisi"
                className={`w-full px-3 py-2 rounded ${
                  errors.noLp ? "border border-red-500" : ""
                }`}
              />
              {errors.noLp && <p className="text-red-400 text-sm mt-1">{errors.noLp}</p>}
            </div>
            {/* ... sisanya sama seperti sebelumnya */}
            {/* Tambahkan input lain step 1 di sini */}
          </>
        )}

        {step === 2 && (
          <>
            {/* Step 2 inputs */}
            {/* Tempat Lahir dan Tanggal Lahir sudah digabung, pekerjaan, agama, alamat, no telepon */}
            <div className="flex space-x-4">
              <div className="flex-1">
                <label className="block mb-1 text-white font-semibold">Tempat Lahir</label>
                <input
                  type="text"
                  name="tempatLahir"
                  value={formData.tempatLahir}
                  onChange={handleChange}
                  placeholder="Masukkan tempat lahir"
                  className={`w-full px-3 py-2 rounded ${
                    errors.tempatLahir ? "border border-red-500" : ""
                  }`}
                />
                {errors.tempatLahir && (
                  <p className="text-red-400 text-sm mt-1">{errors.tempatLahir}</p>
                )}
              </div>

              <div className="flex-1">
                <label className="block mb-1 text-white font-semibold">Tanggal Lahir</label>
                <input
                  type="date"
                  name="tanggalLahir"
                  value={formData.tanggalLahir}
                  onChange={handleChange}
                  className={`w-full px-3 py-2 rounded ${
                    errors.tanggalLahir ? "border border-red-500" : ""
                  }`}
                />
                {errors.tanggalLahir && (
                  <p className="text-red-400 text-sm mt-1">{errors.tanggalLahir}</p>
                )}
              </div>
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Pekerjaan</label>
              <input
                type="text"
                name="pekerjaan"
                value={formData.pekerjaan}
                onChange={handleChange}
                placeholder="Masukkan pekerjaan"
                className={`w-full px-3 py-2 rounded ${
                  errors.pekerjaan ? "border border-red-500" : ""
                }`}
              />
              {errors.pekerjaan && (
                <p className="text-red-400 text-sm mt-1">{errors.pekerjaan}</p>
              )}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Agama</label>
              <select
                name="agama"
                value={formData.agama}
                onChange={handleChange}
                className={`w-full px-3 py-2 rounded text-indigo-900 ${
                  errors.agama ? "border border-red-500" : ""
                }`}
              >
                <option value="">Pilih</option>
                <option value="Islam">Islam</option>
                <option value="Katolik">Katolik</option>
                <option value="Protestan">Protestan</option>
                <option value="Hindu">Hindu</option>
                <option value="Budha">Budha</option>
              </select>
              {errors.agama && (
                <p className="text-red-400 text-sm mt-1">{errors.agama}</p>
              )}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Alamat</label>
              <textarea
                name="alamat"
                value={formData.alamat}
                onChange={handleChange}
                placeholder="Masukkan alamat lengkap"
                rows={3}
                className={`w-full px-3 py-2 rounded resize-none ${
                  errors.alamat ? "border border-red-500" : ""
                }`}
              />
              {errors.alamat && (
                <p className="text-red-400 text-sm mt-1">{errors.alamat}</p>
              )}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">No. Telepon</label>
              <input
                type="text"
                name="noTelepon"
                value={formData.noTelepon}
                onChange={handleChange}
                placeholder="Masukkan nomor telepon"
                className={`w-full px-3 py-2 rounded ${
                  errors.noTelepon ? "border border-red-500" : ""
                }`}
              />
              {errors.noTelepon && (
                <p className="text-red-400 text-sm mt-1">{errors.noTelepon}</p>
              )}
            </div>

            <div className="flex justify-between space-x-4">
              <button
                onClick={handleBack}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              <button
                type="submit"
                className="flex-1 bg-pink-500 hover:bg-pink-600 text-white font-semibold rounded py-2"
              >
                Next
              </button>
            </div>
          </>
        )}

        {step === 3 && (
          <>
            <div>
              <label className="block mb-1 text-white font-semibold">Hari Kejadian</label>
              <input
                type="text"
                name="hariKejadian"
                value={formData.hariKejadian}
                onChange={handleChange}
                placeholder="Masukkan hari kejadian"
                className={`w-full px-3 py-2 rounded ${
                  errors.hariKejadian ? "border border-red-500" : ""
                }`}
              />
              {errors.hariKejadian && (
                <p className="text-red-400 text-sm mt-1">{errors.hariKejadian}</p>
              )}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Tanggal Kejadian</label>
              <input
                type="date"
                name="tanggalKejadian"
                value={formData.tanggalKejadian}
                onChange={handleChange}
                className={`w-full px-3 py-2 rounded ${
                  errors.tanggalKejadian ? "border border-red-500" : ""
                }`}
              />
              {errors.tanggalKejadian && (
                <p className="text-red-400 text-sm mt-1">{errors.tanggalKejadian}</p>
              )}
            </div>

            <div className="flex space-x-4">
              <div className="flex-1">
                <label className="block mb-1 text-white font-semibold">Pukul (Jam)</label>
                <input
                  type="number"
                  name="pukulJam"
                  value={formData.pukulJam}
                  onChange={handleChange}
                  min="0"
                  max="23"
                  placeholder="00 - 23"
                  className={`w-full px-3 py-2 rounded ${
                    errors.pukulJam ? "border border-red-500" : ""
                  }`}
                />
                {errors.pukulJam && (
                  <p className="text-red-400 text-sm mt-1">{errors.pukulJam}</p>
                )}
              </div>

              <div className="flex-1">
                <label className="block mb-1 text-white font-semibold">Pukul (Menit)</label>
                <select
                  name="pukulMenit"
                  value={formData.pukulMenit}
                  onChange={handleChange}
                  className={`w-full px-3 py-2 rounded ${
                    errors.pukulMenit ? "border border-red-500" : ""
                  }`}
                >
                  <option value="">Pilih menit</option>
                  <option value="00">00</option>
                  <option value="30">30</option>
                </select>
                {errors.pukulMenit && (
                  <p className="text-red-400 text-sm mt-1">{errors.pukulMenit}</p>
                )}
              </div>

              <div className="flex-1">
                <label className="block mb-1 text-white font-semibold">Zona Waktu</label>
                <select
                  name="zonaWaktu"
                  value={formData.zonaWaktu}
                  onChange={handleChange}
                  className={`w-full px-3 py-2 rounded ${
                    errors.zonaWaktu ? "border border-red-500" : ""
                  }`}
                >
                  <option value="WIB">WIB</option>
                  <option value="WITA">WITA</option>
                  <option value="WIT">WIT</option>
                </select>
                {errors.zonaWaktu && (
                  <p className="text-red-400 text-sm mt-1">{errors.zonaWaktu}</p>
                )}
              </div>
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Tempat Kejadian</label>
              <input
                type="text"
                name="tempatKejadian"
                value={formData.tempatKejadian}
                onChange={handleChange}
                placeholder="Nama jalan, kec, kab/kota, provinsi"
                className={`w-full px-3 py-2 rounded ${
                  errors.tempatKejadian ? "border border-red-500" : ""
                }`}
              />
              {errors.tempatKejadian && (
                <p className="text-red-400 text-sm mt-1">{errors.tempatKejadian}</p>
              )}
            </div>

            <div>
              <label className="block mb-1 text-white font-semibold">Apa yang terjadi</label>
              <textarea
                name="apaYangTerjadi"
                value={formData.apaYangTerjadi}
                onChange={handleChange}
                placeholder="Jelaskan Apa yang Terjadi"
                rows={4}
                className={`w-full px-3 py-2 rounded resize-none ${
                  errors.apaYangTerjadi ? "border border-red-500" : ""
                }`}
              />
              {errors.apaYangTerjadi && (
                <p className="text-red-400 text-sm mt-1">{errors.apaYangTerjadi}</p>
              )}
            </div>

            <div className="flex justify-between space-x-4">
              <button
                onClick={handleBack}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              <button
                type="submit"
                className="flex-1 bg-pink-500 hover:bg-pink-600 text-white font-semibold rounded py-2"
              >
                Submit
              </button>
            </div>
          </>
        )}
      </form>
    </div>
  );
}
