import React, { useState } from 'react';

export default function Dashboard() {
  const [activeMenu, setActiveMenu] = useState('Dashboard');

  const menuItems = [
    { name: 'Dashboard', icon: 'üìä' },
    { name: 'DIR RES. SIBER', icon: 'üìÅ' },
    { name: 'Input Data', icon: 'üìà' },
    { name: 'Preview Surat', icon: 'üìÑ' },
  ];

  return (
    <div className="flex min-h-screen">
      {/* Sidebar */}
      <div className="bg-white w-60 px-6 py-8 flex flex-col">
        <h1 className="text-xl font-bold text-blue-900 mb-10">e-Perkaba PMJ</h1>
        <h3 className="font-semibold text-gray-700 mb-4">Menu</h3>
        <nav className="flex flex-col space-y-3">
          {menuItems.map(({ name, icon }) => (
            <button
              key={name}
              onClick={() => setActiveMenu(name)}
              className={`flex items-center space-x-2 px-4 py-2 rounded-md border font-semibold text-sm
                ${
                  activeMenu === name
                    ? 'bg-blue-900 text-white border-blue-900'
                    : 'border border-pink-300 text-pink-500 hover:bg-pink-50'
                }`}
            >
              <span>{icon}</span>
              <span>{name}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 bg-blue-900 text-white p-8 relative">
        {/* Header - Profile */}
        <div className="absolute top-6 right-6 flex items-center space-x-3">
          <div className="text-right">
            <div className="font-bold">Karina</div>
            <div className="text-xs text-blue-300">Administrator</div>
          </div>
          <img
            src="https://i.pravatar.cc/40?img=47"
            alt="Profile"
            className="w-10 h-10 rounded-full"
          />
        </div>

        {/* Title */}
        <h2 className="text-lg font-bold mb-6">{activeMenu}</h2>

        {/* Content Area */}
        <div>
          {/* Content sesuai menu bisa ditambah di sini */}
          <p>Selamat datang di {activeMenu}</p>
        </div>
      </div>
    </div>
  );
}
