import React, { useState } from "react";
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

export default function PenyelidikanForm() {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    // Step 1
    noLp: "",
    nama: "",
    noIdentitas: "",
    kewarganegaraan: "",
    jenisKelamin: "",
    // Step 2
    tempatLahir: "",
    tanggalLahir: "",
