                            );
                        })}
                        {formData.selectedPasal.length === 0 && <p className="text-gray-400 text-sm">Belum ada pasal yang dipilih.</p>}
                    </div>
                </div>

                <div className="flex justify-between space-x-4 mt-6">
                    <button onClick={handleBack} className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"> Back </button>
                    {/* Tombol ini sekarang untuk submit akhir */}
                    <button type="submit" className="flex-1 bg-green-500 hover:bg-green-600 text-white font-semibold rounded py-2"> Next </button>
                </div>
            </>
        )}
        {/* ### Step 7 ### */}
        {step === 7 && (
          <>
            <div>
              <textarea
                name="tindakPidana"
                value={formData.tindakPidana}
                onChange={handleChange}
                placeholder="Jelaskan tindak pidana yang terjadi..."
                rows={12}
                className={`w-full px-3 py-2 rounded resize-none bg-gray-200 text-indigo-900 placeholder-gray-500 ${errors.tindakPidana ? "border-2 border-red-500" : "border border-gray-400"}`}
              />
              {errors.tindakPidana && <p className="text-red-400 text-sm mt-1">{errors.tindakPidana}</p>}
            </div>

            <div className="flex justify-between space-x-4 mt-6">
              <button
                onClick={handleBack}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              <button
                type="submit"
                className="flex-1 bg-green-500 hover:bg-green-600 text-white font-semibold rounded py-2"
              >
                Next
              </button>
            </div>
          </>
        )}
        {/* ### Step 8 ### */}
         {step === 8 && (
          <>
            <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6">
              NAMA DAN ALAMAT SAKSI - SAKSI
            </div>

            {/* Menampilkan daftar saksi yang sudah ditambahkan */}
            <div className="mb-6 bg-gray-900 bg-opacity-50 p-4 rounded-lg space-y-2 max-h-40 overflow-y-auto">
              <h4 className="font-bold text-white">Daftar Saksi:</h4>
              {formData.saksi.length > 0 ? (
                <div className="text-white text-sm space-y-3">
                  {formData.saksi.map((saksi, index) => (
                    <p key={index}>
                      {index + 1}. nama/alias: {saksi.nama || '[...]'}, nomor identitas: {saksi.noIdentitas || '[...]'}, ...
                    </p>
                  ))}
                </div>
              ) : (
                <p className="text-gray-400 italic">Belum ada saksi.</p>
              )}
              {errors.saksi && <p className="text-red-400 text-sm mt-1">{errors.saksi}</p>}
            </div>

            {/* Form untuk menambah saksi baru */}
            <div className="space-y-4 border-t-2 border-gray-600 pt-4">
               <h4 className="font-bold text-white">Form Tambah Saksi:</h4>
               <div>
                  <label className="block mb-1 text-sm text-white">Nama / Alias</label>
                  <input type="text" name="nama" value={currentSaksi.nama} onChange={handleSaksiChange} className="w-full px-3 py-2 rounded text-indigo-900" />
               </div>
               <div>
                  <label className="block mb-1 text-sm text-white">No. Identitas</label>
                  <input type="text" name="noIdentitas" value={currentSaksi.noIdentitas} onChange={handleSaksiChange} className="w-full px-3 py-2 rounded text-indigo-900" />
               </div>
               {/* Anda bisa menambahkan field lain untuk saksi di sini */}
               
               <button type="button" onClick={handleAddSaksi} className="w-full bg-blue-500 hover:bg-blue-600 text-white font-semibold rounded py-2">
                + Tambah Saksi
               </button>
            </div>

            {/* Tombol Navigasi Utama */}
            <div className="flex justify-between space-x-4 mt-8">
              <button type="button" onClick={handleBack} className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2">
                Back
              </button>
              {/* Tombol ini akan menjadi tombol SUBMIT final */}
              <button type="submit" className="flex-1 bg-green-500 hover:bg-green-600 text-white font-semibold rounded py-2">
                Next
              </button>
            </div>
          </>
        )}
         {step === 9 && (
          <>
            <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6">
              BARANG BUKTI
            </div>

            <div>
              <textarea
                name="barangBukti"
                value={formData.barangBukti}
                onChange={handleChange}
                placeholder={"1. \n2. \n3. "}
                rows={12}
                className={`w-full px-3 py-2 rounded resize-none bg-gray-200 text-indigo-900 placeholder-gray-500 ${errors.barangBukti ? "border-2 border-red-500" : "border border-gray-400"}`}
              />
              {errors.barangBukti && <p className="text-red-400 text-sm mt-1">{errors.barangBukti}</p>}
            </div>

            <div className="flex justify-between space-x-4 mt-8">
              <button
                type="button"
                onClick={handleBack}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              {/* Tombol ini akan menjadi tombol SUBMIT final */}
              <button
                type="submit"
                className="flex-1 bg-green-500 hover:bg-green-600 text-white font-semibold rounded py-2"
              >
                Next
              </button>
            </div>
          </>
        )}
        {/* ### TAMBAHKAN BLOK JSX BARU INI UNTUK STEP 10 ### */}
        {step === 10 && (
          <>
            <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-4">
              URAIAN SINGKAT YANG DI LAPORKAN
            </div>
            
            {/* Tombol Anonymous */}
            <div className="flex justify-end mb-4">
              <button
                type="button"
                onClick={() => setFormData(prev => ({...prev, isAnonymous: !prev.isAnonymous}))}
                className={`px-4 py-2 text-sm rounded-full text-white font-bold flex items-center transition-colors duration-300 ${formData.isAnonymous ? 'bg-pink-600' : 'bg-gray-500'}`}
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-8.707l-3-3a1 1 0 00-1.414 0l-3 3a1 1 0 001.414 1.414L9 9.414V13a1 1 0 102 0V9.414l1.293 1.293a1 1 0 001.414-1.414z" clipRule="evenodd" />
                </svg>
                Anonymous
              </button>
            </div>

            <div>
              <textarea
                name="uraianSingkat"
                value={formData.uraianSingkat}
                onChange={handleChange}
                placeholder="1. ...
2. ...
3. ..."
                rows={12}
                className={`w-full px-3 py-2 rounded resize-none bg-gray-200 text-indigo-900 placeholder-gray-500 ${errors.uraianSingkat ? "border-2 border-red-500" : ""}`}
              />
              {errors.uraianSingkat && <p className="text-red-400 text-sm mt-1">{errors.uraianSingkat}</p>}
            </div>

            <div className="flex justify-between space-x-4 mt-8">
              <button
                type="button"
                onClick={handleBack}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              <button
                type="submit"
                className="flex-1 bg-green-500 hover:bg-green-600 text-white font-semibold rounded py-2"
              >
                Next
              </button>
            </div>
          </>
        )}
        {/* ### TAMBAHKAN BLOK JSX BARU INI UNTUK STEP 11 (PREVIEW) ### */}
        {step === 11 && (
          <>
            <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6">
              PREVIEW SURAT LAPORAN POLISI
            </div>

            {/* Area yang akan di-screenshot dan dijadikan PDF */}
            <div id="surat-laporan-polisi-preview" className="bg-white p-6 text-black text-xs">
              <div className="text-center font-bold border-b-2 border-black pb-2">
                <p>KEPOLISIAN NEGARA REPUBLIK INDONESIA</p>
                <p>DAERAH METRO JAYA</p>
                <div className="flex justify-between items-end">
                  <span className="text-left">SENTRA PELAYANAN KEPOLISIAN TERPADU</span>
                  <span className="text-right text-sm">"PRO JUSTITIA"</span>
                </div>
              </div>
              <div className="text-center my-4">
                <p className="font-bold underline">LAPORAN POLISI</p>
                <p>Nomor: {formData.noLp || 'LP/B/...'}</p>
              </div>
              
              <p className="font-bold">YANG MELAPORKAN :</p>
              <table className="w-full ml-4 mb-2">
                <tbody>
                  <tr><td className="w-1/3">1. Nama</td><td>: {formData.nama.toUpperCase()}</td></tr>
                  <tr><td>2. No Identitas</td><td>: {formData.noIdentitas}</td></tr>
                  <tr><td>3. Tempat/Tgl Lahir</td><td>: {formData.tempatLahir.toUpperCase()}, {formData.tanggalLahir}</td></tr>
                  <tr><td>4. Jenis Kelamin</td><td>: {formData.jenisKelamin}</td></tr>
                  <tr><td>5. Pekerjaan</td><td>: {formData.pekerjaan.toUpperCase()}</td></tr>
                  {/* ... Tambahkan field lain dari 'YANG MELAPORKAN' di sini */}
                </tbody>
              </table>

              <p className="font-bold">PERISTIWA YANG TERJADI :</p>
              <table className="w-full ml-4 mb-2">
                 <tbody>
                    <tr><td className="w-1/3">1. Waktu Kejadian</td><td>: {formData.hariKejadian}, {formData.tanggalKejadian}</td></tr>
                    <tr><td>2. Tempat Kejadian</td><td>: {formData.tempatKejadian}</td></tr>
                    <tr><td>3. Apa Yang Terjadi</td><td>: {formData.apaYangTerjadi}</td></tr>
                    <tr><td>4. Siapa Terlapor</td><td>: {formData.siapaTerlapor === "Dalam Lidik" ? "DALAM LIDIK" : formData.terlaporNama.toUpperCase()}</td></tr>
                 </tbody>
              </table>

              {/* TABEL UTAMA */}
              <table className="w-full border-collapse border border-black text-xs mt-4">
                <thead>
                  <tr className="bg-gray-200 text-center font-bold">
                    <td className="border border-black p-1">TINDAK PIDANA APA:</td>
                    <td className="border border-black p-1">NAMA DAN ALAMAT SAKSI-SAKSI:</td>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td className="border border-black p-2 align-top w-1/2">{formData.tindakPidana}</td>
                    <td className="border border-black p-2 align-top w-1/2">
                      {Array.isArray(formData.saksi) && formData.saksi.map((saksi, index) => (
                        <p key={index} className="mb-1">{index + 1}. {saksi.nama.toUpperCase()}, ...</p>
                      ))}
                    </td>
                  </tr>
                  <tr className="bg-gray-200 text-center font-bold">
                    <td className="border border-black p-1">BARANG BUKTI:</td>
                    <td className="border border-black p-1">URAIAN SINGKAT YANG DILAPORKAN:</td>
                  </tr>
                  <tr>
                    <td className="border border-black p-2 align-top w-1/2 whitespace-pre-wrap">{formData.barangBukti}</td>
                    <td className="border border-black p-2 align-top w-1/2">{formData.uraianSingkat}</td>
                  </tr>
                </tbody>
              </table>
              {/* Anda bisa menambahkan bagian tanda tangan di sini jika perlu */}
            </div>

            {/* Tombol Navigasi Final */}
            <div className="flex justify-between space-x-4 mt-8">
              <button
                type="button"
                onClick={handleBack}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              <button
                type="button" // Diubah menjadi 'button' karena tidak men-submit form
                onClick={handleDownloadPDF} // Memanggil fungsi download
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded py-2"
              >
                Download Laporan
              </button>
            </div>
          </>
        )}
      </form>
    </div>
  );
};
