          {step < 15 ? ( // Adjusted to 15 as the last step before download
            <button
              type="submit"
              className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded ml-auto"
            >
              Lanjut
            </button>
          ) : (
            <button
              type="button"
              onClick={handleDownloadPDF}
              className="bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded ml-auto"
            >
              Download PDF Gabungan
            </button>
          )}
        </div>
      </form>
    </div>
  );
}
