      // Pusatkan gambar di tengah halaman
      const x = margin;
      const y = (pdfHeight - finalHeightWithMargin) / 2;

      pdf.addImage(imgData, 'PNG', x, y, finalWidthWithMargin, finalHeightWithMargin);
      pdf.save(`Laporan-Polisi-${formData.noLp || 'dokumen'}.pdf`);
