                  </div>
                  <div className="flex-1">
                    <label className="block mb-1 text-sm text-white">Umur Bulan</label>
                    <input type="number" name="umurBulan" value={currentSaksi.umurBulan} onChange={handleSaksiChange} min="0" max="11" placeholder="Bulan" className="w-full px-3 py-2 rounded text-indigo-900" />
                  </div>
                  <div className="flex-1">
                    <label className="block mb-1 text-sm text-white">Umur Hari</label>
                    <input type="number" name="umurHari" value={currentSaksi.umurHari} onChange={handleSaksiChange} min="0" max="30" placeholder="Hari" className="w-full px-3 py-2 rounded text-indigo-900" />
                  </div>
               </div>
               <div>
                  <label className="block mb-1 text-sm text-white">Pekerjaan</label>
                  <input type="text" name="pekerjaan" value={currentSaksi.pekerjaan} onChange={handleSaksiChange} className="w-full px-3 py-2 rounded text-indigo-900" />
               </div>
               <div>
                  <label className="block mb-1 text-sm text-white">Agama</label>
                  <select name="agama" value={currentSaksi.agama} onChange={handleSaksiChange} className="w-full px-3 py-2 rounded text-indigo-900">
                    <option value="">Pilih</option>
                    <option value="Islam">Islam</option>
                    <option value="Katolik">Katolik</option>
                    <option value="Protestan">Protestan</option>
                    <option value="Hindu">Hindu</option>
                    <option value="Budha">Budha</option>
                  </select>
               </div>
               <div>
                  <label className="block mb-1 text-sm text-white">Alamat</label>
                  <textarea name="alamat" value={currentSaksi.alamat} onChange={handleSaksiChange} rows={2} className="w-full px-3 py-2 rounded text-indigo-900 resize-none" />
               </div>
               <div>
                  <label className="block mb-1 text-sm text-white">No. Telp</label>
                  <input type="text" name="noTelp" value={currentSaksi.noTelp} onChange={handleSaksiChange} className="w-full px-3 py-2 rounded text-indigo-900" />
               </div>
               <div>
                  <label className="block mb-1 text-sm text-white">Media Sosial (Platform)</label>
                  <input type="text" name="mediaSosial" value={currentSaksi.mediaSosial} onChange={handleSaksiChange} placeholder="Contoh: Instagram, Facebook" className="w-full px-3 py-2 rounded text-indigo-900" />
               </div>
               
               <button type="button" onClick={handleAddSaksi} className="w-full bg-blue-500 hover:bg-blue-600 text-white font-semibold rounded py-2">
                + Tambah Saksi
               </button>
            </div>

            {/* Tombol Navigasi Utama */}
            <div className="flex justify-between space-x-4 mt-8">
              <button type="button" onClick={handleBack} className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2">
                Back
              </button>
              {/* Tombol ini akan menjadi tombol SUBMIT final */}
              <button type="submit" className="flex-1 bg-green-500 hover:bg-green-600 text-white font-semibold rounded py-2">
                Next
              </button>
            </div>
          </>
        )}
         {step === 9 && (
          <>
            <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6">
              BARANG BUKTI
            </div>

            <div>
              <textarea
                name="barangBukti"
                value={formData.barangBukti}
                onChange={handleChange}
                placeholder={"1. \n2. \n3. "}
                rows={12}
                className={`w-full px-3 py-2 rounded resize-none bg-gray-200 text-indigo-900 placeholder-gray-500 ${errors.barangBukti ? "border-2 border-red-500" : "border border-gray-400"}`}
              />
              {errors.barangBukti && <p className="text-red-400 text-sm mt-1">{errors.barangBukti}</p>}
            </div>

            <div className="flex justify-between space-x-4 mt-8">
              <button
                type="button"
                onClick={handleBack}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              {/* Tombol ini akan menjadi tombol SUBMIT final */}
              <button
                type="submit"
                className="flex-1 bg-green-500 hover:bg-green-600 text-white font-semibold rounded py-2"
              >
                Next
              </button>
            </div>
          </>
        )}
        {/* ### TAMBAHKAN BLOK JSX BARU INI UNTUK STEP 10 ### */}
        {step === 10 && (
          <>
            <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-4">
              URAIAN SINGKAT YANG DI LAPORKAN
            </div>
            
            {/* Tombol Anonymous */}
            <div className="flex justify-end mb-4">
              <button
                type="button"
                onClick={() => setFormData(prev => ({...prev, isAnonymous: !prev.isAnonymous}))}
                className={`px-4 py-2 text-sm rounded-full text-white font-bold flex items-center transition-colors duration-300 ${formData.isAnonymous ? 'bg-pink-600' : 'bg-gray-500'}`}
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-8.707l-3-3a1 1 0 00-1.414 0l-3 3a1 1 0 001.414 1.414L9 9.414V13a1 1 0 102 0V9.414l1.293 1.293a1 1 0 001.414-1.414z" clipRule="evenodd" />
                </svg>
                Anonymous
              </button>
            </div>

            <div>
              <textarea
                name="uraianSingkat"
                value={formData.uraianSingkat}
                onChange={handleChange}
                placeholder="1. ...
2. ...
3. ..."
                rows={12}
                className={`w-full px-3 py-2 rounded resize-none bg-gray-200 text-indigo-900 placeholder-gray-500 ${errors.uraianSingkat ? "border-2 border-red-500" : ""}`}
              />
              {errors.uraianSingkat && <p className="text-red-400 text-sm mt-1">{errors.uraianSingkat}</p>}
            </div>

            <div className="flex justify-between space-x-4 mt-8">
              <button
                type="button"
                onClick={handleBack}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              <button
                type="submit"
                className="flex-1 bg-green-500 hover:bg-green-600 text-white font-semibold rounded py-2"
              >
                Next
              </button>
            </div>
          </>
        )}
        {/* ### TAMBAHKAN BLOK JSX BARU INI UNTUK STEP 11 (PREVIEW) ### */}
        {step === 11 && (
          <>
            <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6">
              PREVIEW SURAT LAPORAN POLISI
            </div>

            {/* Area yang akan di-screenshot dan dijadikan PDF */}
            {/* Mengatur gaya umum untuk seluruh area preview agar mirip dokumen asli */}
            <div id="surat-laporan-polisi-preview" className="bg-white text-black" style={{ fontFamily: 'Times New Roman, serif', padding: '15px 20px', fontSize: '0.65rem', lineHeight: '1.2' }}>
              
              {/* Header: KEPOLISIAN NEGARA ... PRO JUSTITIA */}
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '10px' }}>
                <div style={{ textAlign: 'left', lineHeight: '1.1' }}>
                  <p style={{ fontWeight: 'bold', fontSize: '0.7rem' }}>KEPOLISIAN NEGARA REPUBLIK INDONESIA</p>
                  <p style={{ fontWeight: 'bold', fontSize: '0.7rem' }}>DAERAH METRO JAYA</p>
                  <p style={{ fontWeight: 'bold', fontSize: '0.7rem' }}>SENTRA PELAYANAN KEPOLISIAN TERPADU</p>
                </div>
                <div style={{ textAlign: 'right', lineHeight: '1.1' }}>
                  <p style={{ fontWeight: 'bold', fontSize: '0.65rem' }}>S.I.1.32</p>
                  <p style={{ fontSize: '0.6rem' }}>Model B</p>
                  <p style={{ fontWeight: 'bold', fontSize: '0.7rem', marginTop: '5px' }}>"PRO JUSTITIA"</p>
                </div>
              </div>

              {/* Logo Polda */}
              <div style={{ display: 'flex', justifyContent: 'center', margin: '15px 0' }}>
                {/* Pastikan src di sini sesuai dengan nama file logo Anda di public/images/ */}
                {/* Menggunakan style inline untuk ukuran dan display agar lebih robust */}
                <img src="/images/polda_logo.png" alt="Logo Polda" style={{ width: '50px', height: '50px', margin: '0 auto', display: 'block' }} />
              </div>

              {/* Judul Laporan Polisi */}
              <div style={{ textAlign: 'center', marginBottom: '15px' }}>
                <p style={{ fontWeight: 'bold', textDecoration: 'underline', fontSize: '0.7rem' }}>LAPORAN POLISI</p>
                <p style={{ fontSize: '0.65rem' }}>Nomor: {formData.noLp || 'LP/B/.../SPKT.PMJ'}</p>
              </div>
              
              {/* Bagian YANG MELAPORKAN */}
              <p style={{ fontWeight: 'bold', marginBottom: '5px' }}>YANG MELAPORKAN :</p>
              <table style={{ width: '100%', marginBottom: '10px', fontSize: '0.65rem' }}>
                <tbody>
                  <tr><td style={{ width: '25%', verticalAlign: 'top' }}>1. Nama</td><td style={{ verticalAlign: 'top' }}>: {formData.nama.toUpperCase() || '[NAMA/ALIAS]'}</td></tr>
                  <tr><td style={{ verticalAlign: 'top' }}>2. No Identitas</td><td style={{ verticalAlign: 'top' }}>: {formData.noIdentitas || '[NOMOR IDENTITAS]'}</td></tr>
                  <tr><td style={{ verticalAlign: 'top' }}>3. Kewarganegaraan</td><td style={{ verticalAlign: 'top' }}>: {formData.kewarganegaraan || '[WNI/WNA]'}</td></tr>
                  <tr><td style={{ verticalAlign: 'top' }}>4. Jenis Kelamin</td><td style={{ verticalAlign: 'top' }}>: {formData.jenisKelamin || '[Laki-Laki/Perempuan]'}</td></tr>
                  <tr><td style={{ verticalAlign: 'top' }}>5. Tempat/Tgl Lahir</td><td style={{ verticalAlign: 'top' }}>: {formData.tempatLahir.toUpperCase() || '[TEMPAT]'}, {formData.tanggalLahir ? new Date(formData.tanggalLahir).toLocaleDateString('id-ID', {day: '2-digit', month: '2-digit', year: 'numeric'}) : '[TANGGAL/BLN/TAHUN]'}</td></tr>
                  <tr><td style={{ verticalAlign: 'top' }}>6. Pekerjaan</td><td style={{ verticalAlign: 'top' }}>: {formData.pekerjaan.toUpperCase() || '[PEKERJAAN]'}</td></tr>
                  <tr><td style={{ verticalAlign: 'top' }}>7. Agama</td><td style={{ verticalAlign: 'top' }}>: {formData.agama || '[AGAMA]'}</td></tr>
                  <tr><td style={{ verticalAlign: 'top' }}>8. Alamat</td><td style={{ verticalAlign: 'top' }}>: {formData.alamat || '[ALAMAT]'}</td></tr>
                  <tr><td style={{ verticalAlign: 'top' }}>9. Kontak</td><td style={{ verticalAlign: 'top' }}>: {formData.noTelepon || '[NOMOR HP]'}{formData.mediaSosial ? ` / Email: ${formData.mediaSosial}` : ''}</td></tr>
                </tbody>
              </table>

              {/* Bagian PERISTIWA YANG DILAPORKAN */}
              <p style={{ fontWeight: 'bold', marginTop: '10px', marginBottom: '5px' }}>PERISTIWA YANG DILAPORKAN :</p>
              <table style={{ width: '100%', marginBottom: '10px', fontSize: '0.65rem' }}>
                 <tbody>
                    <tr><td style={{ width: '25%', verticalAlign: 'top' }}>1. Waktu Kejadian</td><td style={{ verticalAlign: 'top' }}>: Hari {formData.hariKejadian || '[HARI]'} Tanggal {formData.tanggalKejadian ? new Date(formData.tanggalKejadian).toLocaleDateString('id-ID', {day: '2-digit', month: '2-digit', year: 'numeric'}) : '[TANGGAL/BLN/TAHUN]'} Pukul {formData.pukulJam || 'HH'}.{formData.pukulMenit || 'MM'} {formData.zonaWaktu || '[WIB/WITA/WIT]'}.</td></tr>
                    <tr><td style={{ verticalAlign: 'top' }}>2. Tempat Kejadian</td><td style={{ verticalAlign: 'top' }}>: {formData.tempatKejadian || '[NAMA JALAN/KEC/KAB/KOTA/PROVINSI]'}</td></tr>
                    <tr>
                      <td style={{ verticalAlign: 'top' }}>3. Apa Yang Terjadi</td>
                      <td style={{ verticalAlign: 'top' }}>
                        : {formData.apaYangTerjadi || '[URAIAN SINGKAT PERISTIWA YANG TERJADI]'}
                      </td>
                    </tr>
                    <tr><td style={{ verticalAlign: 'top' }}>4. Siapa Terlapor</td><td style={{ verticalAlign: 'top', whiteSpace: 'normal', wordBreak: 'break-word' }}>: {formData.siapaTerlapor === "Dalam Lidik" ? "DALAM LIDIK" : 
                      `${formData.terlaporNama.toUpperCase() || '[NAMA/ALIAS]'} (No. Identitas: ${formData.terlaporNoIdentitas || '[NOMOR IDENTITAS]'}, Kewarganegaraan: ${formData.terlaporKewarganegaraan || '[WNI/WNA]'}, Suku: ${formData.terlaporSuku || '[SUKU]'}, Jenis Kelamin: ${formData.terlaporJenisKelamin || '[JENIS KELAMIN]'}, Tempat/Tgl. Lahir: ${formData.terlaporTempatLahir || '[TEMPAT LAHIR]'}, ${formData.terlaporTanggalLahir ? new Date(formData.terlaporTanggalLahir).toLocaleDateString('id-ID', {day: '2-digit', month: '2-digit', year: 'numeric'}) : '[TANGGAL/BLN/TAHUN]'}. Umur: ${formData.terlaporUmurTahun || '00'} Tahun ${formData.terlaporUmurBulan || '00'} Bulan ${formData.terlaporUmurHari || '00'} Hari, Pekerjaan: ${formData.terlaporPekerjaan || '[PEKERJAAN]'}, Agama: ${formData.terlaporAgama || '[AGAMA]'}, Alamat: ${formData.terlaporAlamat || '[ALAMAT]'}, No. Telp: ${formData.terlaporNoTelp || '[NOMOR HP]'}${formData.terlaporMediaSosial ? `, Akun Media Sosial: ${formData.terlaporMediaSosial}` : ''}${formData.terlaporKorporasi ? ` / (KORPORASI)` : ''}).`}
                    </td></tr>
                    <tr><td style={{ verticalAlign: 'top' }}>5. Siapa Korban</td><td style={{ verticalAlign: 'top', whiteSpace: 'normal', wordBreak: 'break-word' }}>: {formData.korbanNama.toUpperCase() || '[NAMA/ALIAS]'} (No. Identitas: ${formData.korbanNoIdentitas || '[NOMOR IDENTITAS]'}, Kewarganegaraan: ${formData.korbanKewarganegaraan || '[WNI/WNA]'}, Suku: ${formData.korbanSuku || '[SUKU]'}, Jenis Kelamin: ${formData.korbanJenisKelamin || '[JENIS KELAMIN]'}, Tempat/Tgl. Lahir: ${formData.korbanTempatLahir || '[TEMPAT LAHIR]'}, ${formData.korbanTanggalLahir ? new Date(formData.korbanTanggalLahir).toLocaleDateString('id-ID', {day: '2-digit', month: '2-digit', year: 'numeric'}) : '[TANGGAL/BLN/TAHUN]'}. Umur: ${formData.korbanUmurTahun || '00'} Tahun ${formData.korbanUmurBulan || '00'} Bulan ${formData.korbanUmurHari || '00'} Hari, Pekerjaan: ${formData.korbanPekerjaan || '[PEKERJAAN]'}, Agama: ${formData.korbanAgama || '[AGAMA]'}, Alamat: ${formData.korbanAlamat || '[ALAMAT]'}, No. Telp: ${formData.korbanNoTelp || '[NOMOR HP]'}${formData.korbanMediaSosial ? `, Akun Media Sosial: ${formData.korbanMediaSosial}` : ''}${formData.korbanKorporasi ? ` / (KORPORASI)` : ''}).
                    </td></tr>
                    <tr><td style={{ verticalAlign: 'top' }}>6. Kapan Dilaporkan</td><td style={{ verticalAlign: 'top' }}>: Hari {formData.kapanDilaporkanHari || '[HARI]'} Tanggal {formData.kapanDilaporkanTanggal ? new Date(formData.kapanDilaporkanTanggal).toLocaleDateString('id-ID', {day: '2-digit', month: '2-digit', year: 'numeric'}) : '[TANGGAL/BLN/TAHUN]'}. Pukul {formData.kapanDilaporkanJam || 'HH'}.{formData.kapanDilaporkanMenit || 'MM'} {formData.kapanDilaporkanZonaWaktu || '[WIB/WITA/WIT]'}.</td></tr>
                 </tbody>
              </table>

              {/* TABEL UTAMA */}
              <table style={{ width: '100%', borderCollapse: 'collapse', border: '1px solid black', marginTop: '15px' }}>
                <thead>
                  <tr>
                    <td style={{ border: '1px solid black', padding: '5px', textAlign: 'center', fontWeight: 'bold', background: '#e0e0e0', width: '50%' }}>TINDAK PIDANA APA:</td>
                    <td style={{ border: '1px black solid', padding: '5px', textAlign: 'center', fontWeight: 'bold', background: '#e0e0e0', width: '50%' }}>NAMA DAN ALAMAT SAKSI-SAKSI:</td>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td style={{ border: '1px solid black', padding: '8px', verticalAlign: 'top', whiteSpace: 'pre-wrap' }}>
                      {formData.tindakPidana || '[URAIAN TINDAK PIDANA]'}
                    </td>
                    <td style={{ border: '1px solid black', padding: '8px', verticalAlign: 'top', whiteSpace: 'normal', wordBreak: 'break-word' }}>
                      {Array.isArray(formData.saksi) && formData.saksi.length > 0 ? (
                        formData.saksi.map((saksi, index) => (
                          <p key={index} style={{ fontSize: '0.6rem', marginBottom: '3px' }}>
                            {index + 1}. Nama/Alias: {saksi.nama?.toUpperCase() || '[...]'}, No. Identitas: {saksi.noIdentitas || '[...]'}, Kewarganegaraan: {saksi.kewarganegaraan || '[...]'}, Suku: {saksi.suku || '[...]'}, Jenis Kelamin: {saksi.jenisKelamin || '[...]'}, Tempat/Tgl. Lahir: {saksi.tempatLahir?.toUpperCase() || '[...]'}, {saksi.tanggalLahir || '[...]'}. Umur: {saksi.umurTahun || '00'} Tahun {saksi.umurBulan || '00'} Bulan {saksi.umurHari || '00'} Hari, Pekerjaan: {saksi.pekerjaan?.toUpperCase() || '[...]'}, Agama: {saksi.agama || '[...]'}, Alamat: {saksi.alamat || '[...]'}, No. Telp: {saksi.noTelp || '[...]'}, Akun Media Sosial: {saksi.mediaSosial || '[...]'}.
                          </p>
                        ))
                      ) : (
                        <p style={{ fontSize: '0.6rem' }}>Tidak ada saksi yang ditambahkan.</p>
                      )}
                    </td>
                  </tr>
                  <tr style={{ background: '#e0e0e0' }}>
                    <td style={{ border: '1px solid black', padding: '5px', textAlign: 'center', fontWeight: 'bold' }}>BARANG BUKTI:</td>
                    <td style={{ border: '1px black solid', padding: '5px', textAlign: 'center', fontWeight: 'bold' }}>URAIAN SINGKAT YANG DILAPORKAN:</td>
                  </tr>
                  <tr>
                    <td style={{ border: '1px solid black', padding: '8px', verticalAlign: 'top', whiteSpace: 'pre-wrap' }}>{formData.barangBukti || '[BARANG BUKTI]'}</td>
                    <td style={{ border: '1px solid black', padding: '8px', verticalAlign: 'top', whiteSpace: 'pre-wrap' }}>{formData.uraianSingkat || '[URAIAN SINGKAT]'}</td>
                  </tr>
                </tbody>
              </table>

              {/* Bagian Pelapor dan Tanda Tangan */}
              <p style={{ marginTop: '15px', fontSize: '0.65rem' }}>Pelapor atau pengadu memberikan keterangan, kemudian membubuhkan tandatangannya di bawah ini.</p>
              <div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: '15px', marginRight: '50px' }}>
                <div style={{ textAlign: 'center' }}>
                  <p style={{ fontWeight: 'bold' }}>Pelapor</p>
                  <p style={{ marginTop: '30px', fontSize: '0.65rem' }}>({formData.isAnonymous ? 'ANONYMOUS' : formData.nama.toUpperCase()})</p>
                </div>
              </div>

              {/* Bagian Tindakan yang Dilakukan */}
              <p style={{ fontWeight: 'bold', marginTop: '20px', fontSize: '0.65rem' }}>TINDAKAN YANG DILAKUKAN :</p>
              <ol style={{ listStyleType: 'decimal', marginLeft: '20px', fontSize: '0.65rem', lineHeight: '1.4' }}>
                <li>Membuat Laporan Polisi.</li>
                <li>Membuat tanda bukti laporan.</li>
                <li>Menerima barang bukti.</li>
              </ol>

              {/* Bagian Piket Siaga dan Yang Menerima Laporan (Pangkat & NRP) */}
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-end', marginTop: '40px', fontSize: '0.65rem' }}>
                <div style={{ textAlign: 'left' }}>
                  <p>Piket Siaga</p>
                  <p style={{ marginTop: '30px' }}>NAMA</p>
                  <p>PANGKAT NRP</p>
                  <p>0000000</p> {/* Template untuk Piket Siaga jika tidak ada input */}
                </div>
                <div style={{ textAlign: 'right' }}>
                  <p>Jakarta, {new Date().toLocaleDateString('id-ID', { day: '2-digit', month: 'long', year: 'numeric' })}</p>
                  <p style={{ fontWeight: 'bold', marginTop: '5px' }}>Yang Menerima Laporan</p>
                  <p style={{ fontWeight: 'bold' }}>Penyelidik / Penyidik</p>
                  <p style={{ marginTop: '30px' }}>NAMA</p>
                  <p>
                    {formData.pangkatPenyidik ? formData.pangkatPenyidik.toUpperCase() : 'PANGKAT'}
                    {' '}
                    {formData.nrpPenyidik ? 'NRP ' + formData.nrpPenyidik : 'NRP'}
                  </p>
                </div>
              </div>
            </div> {/* Penutup div untuk id="surat-laporan-polisi-preview" */}

            {/* Tombol Navigasi Final */}
            <div className="flex justify-between space-x-4 mt-8">
              <button
                type="button"
                onClick={handleBack}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              <button
                type="button" // Diubah menjadi 'button' karena tidak men-submit form
                onClick={handleDownloadPDF} // Memanggil fungsi download
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded py-2"
              >
                Download Laporan
              </button>
            </div>
          </>
        )}
      </form>
    </div>
  );
};
