import React, { useState } from "react";
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

export default function PenyelidikanForm() {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    noLp: "",
    nama: "",
    noIdentitas: "",
    kewarganegaraan: "",
    jenisKelamin: "",
    tempatLahir: "",
    tanggalLahir: "",
    pekerjaan: "",
    agama: "",
    alamat: "",
    noTelepon: "",
    hariKejadian: "",
    tanggalKejadian: "",
    pukulJam: "",
    pukulMenit: "",
    zonaWaktu: "WIB",
    tempatKejadian: "",
    apaYangTerjadi: "",
    siapaTerlapor: "",
    terlaporNama: "",
    terlaporNoIdentitas: "",
    terlaporKewarganegaraan: "",
    terlaporSuku: "",
    terlaporJenisKelamin: "",
    terlaporTempatLahir: "",
    terlaporTanggalLahir: "",
    terlaporUmurTahun: "",
    terlaporUmurBulan: "",
    terlaporUmurHari: "",
    terlaporPekerjaan: "",
    terlaporAgama: "",
    terlaporAlamat: "",
    terlaporNoTelp: "",
    terlaporMediaSosial: "",
    terlaporKorporasi: false,
    korbanNama: "",
    korbanNoIdentitas: "",
    korbanKewarganegaraan: "",
    korbanSuku: "",
    korbanJenisKelamin: "",
    korbanTempatLahir: "",
    korbanTanggalLahir: "",
    korbanUmurTahun: "",
    korbanUmurBulan: "",
    korbanUmurHari: "",
    korbanPekerjaan: "",
    korbanAgama: "",
    korbanAlamat: "",
    korbanNoTelp: "",
    korbanMediaSosial: "",
    korbanKorporasi: false,
    kapanDilaporkanHari: "",
    kapanDilaporkanTanggal: "",
    kapanDilaporkanJam: "",
    kapanDilaporkanMenit: "",
    kapanDilaporkanZonaWaktu: "WIB",
    selectedPasal: [],
    tindakPidana: "",
    saksi: [],
    barangBukti: "",
    uraianSingkat: "",
    isAnonymous: false,
  });

  const [currentSaksi, setCurrentSaksi] = useState({
    nama: '',
    noIdentitas: '',
    kewarganegaraan: '',
    suku: '',
    jenisKelamin: '',
    tempatLahir: '',
    tanggalLahir: '',
    umurTahun: '',
    umurBulan: '',
    umurHari: '',
    pekerjaan: '',
    agama: '',
    alamat: '',
    noTelp: '',
    mediaSosial: '',
  });

  const [errors, setErrors] = useState({});

  const availablePasal = [
    { id: "pasal_45_27_1", text: "Pasal 45 Jo. Pasal 27 ayat (1)" },
    { id: "pasal_45_27_2", text: "Pasal 45 Jo. Pasal 27 ayat (2)" },
    { id: "pasal_45_27a", text: "Pasal 45 Jo. Pasal 27A" },
    { id: "pasal_45_27b_1", text: "Pasal 45 Jo. Pasal 27B ayat (1)" },
    { id: "pasal_45_27b_2", text: "Pasal 45 Jo. Pasal 27B ayat (2)" },
    { id: "pasal_45a_28_1", text: "Pasal 45A Jo. Pasal 28 ayat (1)" },
    { id: "pasal_45a_28_2", text: "Pasal 45A Jo. Pasal 28 ayat (2)" },
    { id: "pasal_45a_29_1", text: "Pasal 45A Jo. Pasal 29 ayat (1)" },
    { id: "pasal_45b_29", text: "Pasal 45B Jo. Pasal 29" },
    { id: "pasal_46_30_1", text: "Pasal 46 Jo. Pasal 30 ayat (1)" },
    { id: "pasal_46_30_2", text: "Pasal 46 Jo. Pasal 30 ayat (2)" },
    { id: "pasal_47_31_1", text: "Pasal 47 Jo. Pasal 31 ayat (1)" },
    { id: "pasal_47_31_2", text: "Pasal 47 Jo. Pasal 31 ayat (2)" },
    { id: "pasal_48_32_1", text: "Pasal 48 Jo. Pasal 32 ayat (1)" },
    { id: "pasal_48_32_2", text: "Pasal 48 Jo. Pasal 32 ayat (2)" },
    { id: "pasal_48_32_3", text: "Pasal 48 Jo. Pasal 32 ayat (3)" },
    { id: "pasal_49_33", text: "Pasal 49 Jo. Pasal 33" },
    { id: "pasal_50_34_1", text: "Pasal 50 Jo. Pasal 34 ayat (1)" },
    { id: "pasal_51_35", text: "Pasal 51 Jo. Pasal 35" },
    { id: "pasal_51_36", text: "Pasal 51 Jo. Pasal 36" },
  ];

  // Validation functions omitted for brevity; implement as in context code

  // For brevity in this snippet, validation and handleNext, handleBack etc are assumed same as prior context and correct

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handlePasalChange = (e) => {
    const { id, checked } = e.target;
    setFormData(prev => {
        const newSelectedPasal = [...prev.selectedPasal];
        if (checked && !newSelectedPasal.includes(id)) {
            newSelectedPasal.push(id);
        } else if (!checked) {
            const index = newSelectedPasal.indexOf(id);
            if (index > -1) {
                newSelectedPasal.splice(index, 1);
            }
        }
        return { ...prev, selectedPasal: newSelectedPasal };
    });
  };

  const handleSaksiChange = (e) => {
    const { name, value } = e.target;
    setCurrentSaksi(prev => ({ ...prev, [name]: value }));
  };

  const handleAddSaksi = (e) => {
    e.preventDefault();
    if (!currentSaksi.nama) {
      alert("Nama saksi tidak boleh kosong.");
      return;
    }
    setFormData(prev => ({ ...prev, saksi: [...prev.saksi, currentSaksi] }));
    setCurrentSaksi({
      nama: '',
      noIdentitas: '',
      kewarganegaraan: '',
      suku: '',
      jenisKelamin: '',
      tempatLahir: '',
      tanggalLahir: '',
      umurTahun: '',
      umurBulan: '',
      umurHari: '',
      pekerjaan: '',
      agama: '',
      alamat: '',
      noTelp: '',
      mediaSosial: '',
    });
  };

  const handleDownloadPDF = () => {
    const input = document.getElementById('surat-laporan-polisi-preview');
    if(!input) {
      alert("Elemen pratinjau tidak ditemukan!");
      return;
    }
    html2canvas(input, {scale:2, useCORS:true}).then(canvas=>{
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF({unit:'mm', format:'a4', orientation:'portrait'});
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = pdf.internal.pageSize.getHeight();
      const canvasWidth = canvas.width;
      const canvasHeight = canvas.height;
      const ratio = canvasWidth / canvasHeight;
      let imgWidth = pdfWidth - 20;
      let imgHeight = imgWidth / ratio;
      if(imgHeight > pdfHeight - 20) {
        imgHeight = pdfHeight - 20;
        imgWidth = imgHeight * ratio;
      }
      const x = (pdfWidth - imgWidth) / 2;
      const y = (pdfHeight - imgHeight) / 2;
      pdf.addImage(imgData, 'PNG', x, y, imgWidth, imgHeight);
      pdf.save(`Laporan-Polisi-${formData.noLp||'dokumen'}.pdf`);
    });
  };

  // Handle navigation, submission, validations (omitted here for brevity but same as provided)

  // Code to split pasal for columns:
  const middleIndex = Math.ceil(availablePasal.length / 2);
  const firstColumnPasal = availablePasal.slice(0, middleIndex);
  const secondColumnPasal = availablePasal.slice(middleIndex);

  return (
    <div className="flex justify-center mt-10">
      <form onSubmit={(e) => {
        e.preventDefault();
        if (step < 11) {
          // Validate and move next step (implement validation as in your app)
          // For simplicity, immediately go next here
          setStep((prev) => Math.min(prev + 1, 11));
        }
      }} className="bg-indigo-800 p-8 rounded-md max-w-md w-full space-y-6 text-indigo-900" >
        {/* Include multi-step form UI here, at step 11 show preview */}

        {step === 11 && (
          <>
            <div className="bg-white text-indigo-900 font-semibold rounded-xl px-4 py-2 text-center select-none mb-6">
              PREVIEW SURAT LAPORAN POLISI
            </div>

            <div
              id="surat-laporan-polisi-preview"
              className="bg-white p-6 text-black text-xs"
              style={{ fontFamily: 'Times New Roman, serif', padding: '20px' }}
            >
              <div className="flex justify-between items-start mb-4">
                <div className="text-left leading-tight" style={{fontSize: '9px'}}>
                  <p className="font-bold">KEPOLISIAN NEGARA REPUBLIK INDONESIA</p>
                  <p className="font-bold">DAERAH METRO JAYA</p>
                  <p className="font-bold">SENTRA PELAYANAN KEPOLISIAN TERPADU</p>
                </div>
                <div className="text-right leading-tight" style={{fontSize: '8px'}}>
                  <p className="font-bold">S.I.1.32</p>
                  <p>Model B</p>
                  <p className="font-bold mt-2">"PRO JUSTITIA"</p>
                </div>
              </div>
              <div className="flex justify-center my-4">
                <img
                  src="/images/polda_logo.png"
                  alt="Logo Polda Metro Jaya"
                  style={{ width: '64px', height: '64px', display: 'block', margin: '0 auto' }}
                  onError={(e)=>{e.target.onerror=null; e.target.src='https://storage.googleapis.com/workspace-0f70711f-8b4e-4d94-86f1-2a93ccde5887/image/7dfda338-12af-47f3-94a6-a56485a41b64.png';}}
                />
              </div>
              <div className="text-center my-4" style={{fontSize:'10px'}}>
                <p className="font-bold underline tracking-wide">LAPORAN POLISI</p>
                <p>Nomor: {formData.noLp || 'LP/B/.../SPKT.PMJ'}</p>
              </div>

              <p className="font-bold mt-2 mb-1">YANG MELAPORKAN :</p>
              <table className="w-full ml-2 mb-4" style={{fontSize: '8px'}}>
                <tbody>
                  <tr><td style={{width:'30%'}}>1. Nama</td><td>: {formData.nama.toUpperCase() || '[...]'}</td></tr>
                  <tr><td>2. No Identitas</td><td>: {formData.noIdentitas || '[...]'}</td></tr>
                  <tr><td>3. Kewarganegaraan</td><td>: {formData.kewarganegaraan || '[...]'}</td></tr>
                  <tr><td>4. Jenis Kelamin</td><td>: {formData.jenisKelamin || '[...]'}</td></tr>
                  <tr><td>5. Tempat/Tgl Lahir</td><td>: {formData.tempatLahir.toUpperCase() || '[...]'}, {formData.tanggalLahir || '[...]'}</td></tr>
                  <tr><td>6. Pekerjaan</td><td>: {formData.pekerjaan.toUpperCase() || '[...]'}</td></tr>
                  <tr><td>7. Agama</td><td>: {formData.agama || '[...]'}</td></tr>
                  <tr><td>8. Alamat</td><td>: {formData.alamat || '[...]'}</td></tr>
                  <tr><td>9. Kontak</td><td>: {formData.noTelepon || '[...]'}</td></tr>
                </tbody>
              </table>

              <p className="font-bold mt-2 mb-1">PERISTIWA YANG DILAPORKAN :</p>
              <table className="w-full ml-2 mb-4" style={{fontSize: '8px'}}>
                <tbody>
                  <tr><td style={{width:'30%'}}>1. Waktu Kejadian</td><td>
                    : Pukul {formData.pukulJam || '00'}.{formData.pukulMenit || '00'} {formData.zonaWaktu || 'WIB'}, Hari {formData.hariKejadian || '[...]'}, Tanggal {formData.tanggalKejadian || '[...]'}.
                  </td></tr>
                  <tr><td>2. Tempat Kejadian</td><td>: {formData.tempatKejadian || '[...]'}.</td></tr>
                  <tr><td>3. Apa Yang Terjadi</td><td>: {formData.apaYangTerjadi || '[...]'}.</td></tr>
                  <tr><td>4. Siapa Terlapor</td><td>: {formData.siapaTerlapor === "Dalam Lidik" ? "DALAM LIDIK" : 
                      `${formData.terlaporNama.toUpperCase() || '[...]'} (No. Identitas: ${formData.terlaporNoIdentitas || '[...]'}, Kewarganegaraan: ${formData.terlaporKewarganegaraan || '[...]'}, Suku: ${formData.terlaporSuku || '[...]'}, Jenis Kelamin: ${formData.terlaporJenisKelamin || '[...]'}, Tempat/Tgl Lahir: ${formData.terlaporTempatLahir || '[...]'}, ${formData.terlaporTanggalLahir || '[...]'}. Umur: ${formData.terlaporUmurTahun || '00'} Tahun ${formData.terlaporUmurBulan || '00'} Bulan ${formData.terlaporUmurHari || '00'} Hari, Pekerjaan: ${formData.terlaporPekerjaan || '[...]'}, Agama: ${formData.terlaporAgama || '[...]'}, Alamat: ${formData.terlaporAlamat || '[...]'}, No. Telp: ${formData.terlaporNoTelp || '[...]'}${formData.terlaporMediaSosial ? `, Akun Media Sosial: ${formData.terlaporMediaSosial}` : ''}${formData.terlaporKorporasi ? ` / KORPORASI` : ''}).`
                    }
                  </td></tr>
                  <tr><td>5. Siapa Korban</td><td>: {formData.korbanNama.toUpperCase() || '[...]'} (No. Identitas: {formData.korbanNoIdentitas || '[...]'}, Kewarganegaraan: {formData.korbanKewarganegaraan || '[...]'}, Suku: {formData.korbanSuku || '[...]'}, Jenis Kelamin: {formData.korbanJenisKelamin || '[...]'}, Tempat/Tgl Lahir: {formData.korbanTempatLahir || '[...]'}, {formData.korbanTanggalLahir || '[...]'}. Umur: {formData.korbanUmurTahun || '00'} Tahun {formData.korbanUmurBulan || '00'} Bulan {formData.korbanUmurHari || '00'} Hari, Pekerjaan: {formData.korbanPekerjaan || '[...]'}, Agama: {formData.korbanAgama || '[...]'}, Alamat: {formData.korbanAlamat || '[...]'}, No. Telp: {formData.korbanNoTelp || '[...]'}{formData.korbanMediaSosial ? `, Akun Media Sosial: ${formData.korbanMediaSosial}` : ''}{formData.korbanKorporasi ? ` / KORPORASI` : ''}).
                  </td></tr>
                  <tr><td>6. Kapan Dilaporkan</td><td>: Hari {formData.kapanDilaporkanHari || '[...]'}, Tanggal {formData.kapanDilaporkanTanggal || '[...]'}. Pukul {formData.kapanDilaporkanJam || '00'}.{formData.kapanDilaporkanMenit || '00'} {formData.kapanDilaporkanZonaWaktu || 'WIB'}.</td></tr>
                </tbody>
              </table>

              <table className="w-full border-collapse border border-black text-xs mt-4" style={{fontSize:'8px'}}>
                <thead>
                  <tr className="bg-gray-200 text-center font-bold">
                    <td className="border border-black p-1 w-1/2">TINDAK PIDANA APA:</td>
                    <td className="border border-black p-1 w-1/2">NAMA DAN ALAMAT SAKSI - SAKSI:</td>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td className="border border-black p-2 align-top" style={{ whiteSpace: 'pre-wrap' }}>{formData.tindakPidana}</td>
                    <td className="border border-black p-2 align-top" style={{ whiteSpace: 'normal' }}>
                      {formData.saksi.length > 0 ? (
                        formData.saksi.map((saksi, index) => (
                          <p key={index} style={{ fontSize: '7px', marginBottom: '2px', wordBreak: 'break-word' }}>
                            {index + 1}. Nama/Alias: {saksi.nama.toUpperCase() || '[...]'}, No. Identitas: {saksi.noIdentitas || '[...]'}, Kewarganegaraan: {saksi.kewarganegaraan || '[...]'}, Suku: {saksi.suku || '[...]'}, Jenis Kelamin: {saksi.jenisKelamin || '[...]'}, Tempat/Tgl Lahir: {saksi.tempatLahir.toUpperCase() || '[...]'}, {saksi.tanggalLahir || '[...]'}. Umur: {saksi.umurTahun || '00'} Tahun {saksi.umurBulan || '00'} Bulan {saksi.umurHari || '00'} Hari, Pekerjaan: {saksi.pekerjaan.toUpperCase() || '[...]'}, Agama: {saksi.agama || '[...]'}, Alamat: {saksi.alamat || '[...]'}, No. Telp: {saksi.noTelp || '[...]'}, Akun Media Sosial: {saksi.mediaSosial || '[...]'}.
                          </p>
                        ))
                      ) : (
                        <p>Tidak ada saksi.</p>
                      )}
                    </td>
                  </tr>
                  <tr className="bg-gray-200 text-center font-bold" style={{fontSize:'8px'}}>
                    <td className="border border-black p-1">BARANG BUKTI:</td>
                    <td className="border border-black p-1">URAIAN SINGKAT YANG DILAPORKAN:</td>
                  </tr>
                  <tr>
                    <td className="border border-black p-2 align-top whitespace-pre-wrap">{formData.barangBukti}</td>
                    <td className="border border-black p-2 align-top whitespace-pre-wrap">{formData.uraianSingkat}</td>
                  </tr>
                </tbody>
              </table>

              <p className="mt-4 text-xs">Pelapor atau pengadu memberikan keterangan, kemudian membubuhkan tandatangannya di bawah ini.</p>
              <div className="flex justify-end mt-4">
                <div className="text-center">
                  <p className="font-bold">Pelapor</p>
                  <p className="mt-8">({formData.isAnonymous ? 'ANONYMOUS' : formData.nama.toUpperCase()})</p>
                </div>
              </div>

              <p className="font-bold mt-4 text-xs">TINDAKAN YANG DILAKUKAN :</p>
              <ol className="list-decimal list-inside ml-4 text-xs" style={{fontSize:'8px'}}>
                <li>Membuat Laporan Polisi</li>
                <li>Membuat tanda bukti lapor.</li>
                <li>Menerima barang bukti.</li>
              </ol>

              <div className="flex justify-between items-start mt-8 text-xs" style={{fontSize:'8px'}}>
                <div>
                  <p>Piket Siaga</p>
                  <p className="mt-8">Nama</p>
                  <p>PANGKAT NRP 0000000</p>
                </div>
                <div className="text-right">
                  <p>Jakarta, {new Date().toLocaleDateString('id-ID', {day:'2-digit', month:'long', year:'numeric'})}</p>
                  <p className="font-bold">Yang Menerima Laporan</p>
                  <p className="font-bold">Penyelidik / Penyidik</p>
                  <p className="mt-8">Nama</p>
                  <p>PANGKAT NRP 0000000</p>
                </div>
              </div>
            </div>

            <div className="flex justify-between space-x-4 mt-8">
              <button
                type="button"
                onClick={() => setStep(10)}
                className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-semibold rounded py-2"
              >
                Back
              </button>
              <button
                type="button"
                onClick={handleDownloadPDF}
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded py-2"
              >
                Download Laporan
              </button>
            </div>
          </>
        )}

        {/* You can implement UI for other steps here based on step number */}

      </form>
    </div>
  );
}
