      setErrors({});
      setStep(3);
    } else if (step === 3 && validateStep3()) {
      setErrors({});
      setStep(4);
    } else if (step === 4 && validateStep4()) {
      setErrors({});
      setStep(5);
    }
  };

  const handleBack = (e) => {
    e.preventDefault();
    setErrors({});
    if (step > 1) setStep(step - 1);
